import React from "react";
import OutsideAlerter from "./OutsideAlerter"
import profileImg from "../images/user.png"
import axios from "axios"

export default function Header(props) {

  const [accountMenuDisplay, setAccountMenuDisplay] = React.useState("none");

  const headerStyle = {
    gridArea: `header`,
    backgroundColor: `white`,
    display: `flex`,
    alignItems: `center`,
    justifyContent: `flex-end`,
    padding: `0 40px`,
    borderBottom: "1px solid rgba(30,56,77, .2)", //same as border-top for .sidenav in App.css
    // boxShadow: "0 1px 4px rgba(0, 0, 0, 0.2),  0 10px rgba(10, 0, 0, 0.1)" 
  };

  const iconStyle = {
    height: "15px",
    paddingRight: "5px"
  };

  const profileStyle = {
    backgroundColor: `white`,
  }

  const accountMenuStyle = {
    position: "absolute",
    top: "50px",
    right: "25px",
    zIndex: "10",
    border: "1px rgb(228, 233, 242) solid",
    backgroundColor: "white",
    borderRadius: "0.17rem",
    cursor: "pointer"
  };

  const accountMenuItemStyle = {
    borderBottom: "1px solid rgb(228, 233, 242)",
    padding: "20px 40px",
    fontSize: "12px",
  };

  function openAccountMenu(e) {
    console.log(e.target)
    // console.log('wtf is happening')
    if (accountMenuDisplay === "none") {
      setAccountMenuDisplay("block");
    } else {
      setAccountMenuDisplay("none");
    }
  }

  function logout() {

    axios.get('http://localhost:5000/user/logout/', {withCredentials: true}).then(res => {
      if (res.data === "Success") {        
        // console.log('successful logout')
      } else {
        alert("Failed to log out. Please try again.")
      }
    })}

  return (
    <header style={headerStyle}>

    <div style={{cursor: "pointer"}} onClick={openAccountMenu}>
      <img
        style={iconStyle}
        src={profileImg}
        alt="profile" />
      {props.user.firstName + " " + props.user.lastName}
      <OutsideAlerter>
        <div style={accountMenuStyle}>
          <div style={accountMenuItemStyle}>
            <a href="/login" onClick={logout}>Logout</a>
          </div>
        </div>
      </OutsideAlerter>
    </div>


    </header>
  );
}
