import React, {useEffect, useState} from "react"
import Layout from "../components/Layout"
import Table from "../components/Table"
import DownloadBtn from "../components/DownloadBtn"
import * as router from "./productAPI"

export default function Product(props) {

  const [products, setProducts] = useState([])
  const [isLoading, setLoading] = useState(false)

  const outerStyle = {
    gridArea: "main",
    backgroundColor: "rgb(246,246,247)"
  }

  const innerStyle = {
    backgroundColor: "white",
    width: "80%", 
    borderRadius: "5px",
    margin: "20px auto",
    padding: "1% 3%",
    boxShadow:
  `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
  }

  const columns = React.useMemo(
    () => [
      {
        Header: 'ID',
        accessor: 'id',
        show: false
      },
      {
        Header: 'Product Name',
        accessor: 'title',
        sortDescFirst: true
      },
      {
        Header: 'Shopify ID',
        accessor: 'shopify_id',
        show: false
      },
      {
        Header: 'Vendor',
        accessor: 'vendor',
        sortDescFirst: true
      },
      {
        Header: 'Vendor_ID',
        accessor: 'vendor_id',
        show: false
      },
      {
        Header: 'Variants',
        accessor: 'variants',
        sortDescFirst: true
      },
      {
        Header: 'Quantity',
        accessor: 'quantity',
        sortDescFirst: true
      },
      // {
      //   Header: 'Purchases',
      //   accessor: 'purchases',
      //   sortDescFirst: true
      // },
      {
        Header: 'Purchases/day',
        accessor: 'salesPerDay',
        sortDescFirst: true
      },
      {
        Header: 'Stock Level',
        accessor: 'stockLevel',
        disableSortBy: true
      },
    ],
    []
  )

  // console.log(products)

  function getData(reload) {
    setLoading(true)
    if (props.location.state) {
      if (reload) {
        router.loadLowProducts().then(data => {
          if (data) {
              setProducts(data)
          }
          setLoading(false) 
        })
      } else {
        setProducts(props.location.state.products)
        setLoading(false) 
      }
    } else {
      router.loadProducts().then(data => {
        if (data) {
          setProducts(data)
        }
        setLoading(false) 
      })
    }  
  }

    useEffect(() => {
      getData()
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    if (isLoading) {
      return <div className="loader"></div>;
    }


  return (
    <Layout user={props.user}>
      <div style={outerStyle}>
        <div style={innerStyle}>
          <h1>Products</h1>
          <DownloadBtn data={products} type="products" />
          <Table reloadData={() => getData('reload')} initialSearch="" type="products" columns={columns} data={products} search={true} popupEnabled={true} perPage={10}/>
        </div>
      </div>
    </Layout>
  )
}
