import {useState, useEffect} from "react"
import axios from "axios"
import delta8Image from "../images/delta8-thc-products.jpg"
export default function Popup(props) {

    const [isEditing, setIsEditing] = useState(false)
    const [editColor, setEditColor] = useState("#76c32d")
    const [btnText, setBtnText] = useState("Edit")

    // console.log(props.product.row)

    const productData = props.product.row.values

    const [product, setProduct] = useState({})

    function getProduct() {
        axios.get('http://localhost:5000/products/'+productData.id, {withCredentials: true}).then(res => {
            console.log(res)
            if (res.data) {
               console.log("Get product data:")
               console.log(res.data)
                setProduct(res.data)

            } else {
                console.log("Error getting product data")
            }



        //   if (response.data.user) {
        //     console.log('Get User: There is a user saved in the server session: ')
        //     setUser(response.data.user)
        //     setLoggedIn(true)
        //   } else {
        //     console.log('Get user: no user')

        //     setUser(response.data.user)
        //     setLoggedIn(false)
        //   }
        //   setLoading(false)
        });
      }

      useEffect(() => {
        getProduct()
      }, []);

    console.log(product)


    // console.log(props)
    function handleClick() {
        props.toggle()
    }

    function editClicked(e) {
        console.log('clickedd')
        console.log(e.target)
        setIsEditing(!isEditing)
        setEditColor(editColor === "#26B1FF" ? "#76c32d" : "#26B1FF")
        setBtnText(btnText === "Edit" ? "Save" : "Edit")
        // e.target.backgroundColor = "#000000"
    }
    const editBtnStyle = {
        color: "white",
        backgroundColor: editColor,
        padding: "7px 30px",
        borderRadius: "5px",
        border: "none",
        fontSize: "16px",
        float: "right"
    }
    const saveBtnStyle = {

    }

    if (isLoading) {
        return <div className="loader"></div>;
      }


    return (
        <div className="modal">
            <div className="modal_content">
                <span className="close" onClick={handleClick}>&times;    </span>
                <p></p>
                <img style={{width: "10%"}} src={product.image} alt="product image"/>
                <h2>{product.title}</h2>



                <h3 >Variants</h3>
                <button onClick={editClicked} style={editBtnStyle}>{btnText}</button>
                <table style={{textAlign: "center"}}>
                    <thead >
                        <tr>
                            <th style={{paddingBottom: "10px"}}>Variant</th>
                            <th style={{paddingBottom: "10px"}}>Price</th>
                            <th style={{paddingBottom: "10px"}}>Quantity</th>
                            <th style={{paddingBottom: "10px"}}> </th>
                        </tr>
                    </thead>

                    <tbody>
                    {/* {product.variants.map((variant, index) => {
                            return (
                                <tr>
                                    <td style={{paddingRight: "20px", border: "none"}}>{variant.title}</td>
                                    <td style={{paddingRight: "20px", border: "none"}}>30</td>
                                    {isEditing ? <td style={{paddingRight: "20px", border: "none"}}><input value={1}></input></td>: <td style={{paddingRight: "20px", border: "none"}}>1</td>}

                                </tr>
                            ) 


                        })


                    } */}
                        {/* {product.variants.length > 1 ? product.variants.map((variant, index) => {
                            return (
                                <tr>
                                    <td style={{paddingRight: "20px", border: "none"}}>{variant.title}</td>
                                    <td style={{paddingRight: "20px", border: "none"}}>30</td>
                                    {isEditing ? <td style={{paddingRight: "20px", border: "none"}}><input value={1}></input></td>: <td style={{paddingRight: "20px", border: "none"}}>1</td>}

                                </tr>
                            ) 


                        })
                        :
                        <p>No variants</p>
                    } */}
                    {/* {sortedTopSellingProducts.map((item, index) => {
                        return (
                        <tr key={index} >
                        <td style={{paddingRight: "20px"}}>{item[0]}</td>
                        <td style={{paddingRight: "20px"}}>{item[1]}</td>
                        </tr>
                        )
                    })
                    } */}
                        <tr >
                            <td style={{paddingRight: "20px", border: "none"}}>700mg</td>
                            <td style={{paddingRight: "20px", border: "none"}}>30</td>
                            {isEditing ? <td style={{paddingRight: "20px", border: "none"}}><input value={1}></input></td>: <td style={{paddingRight: "20px", border: "none"}}>1</td>}

                            {/* <td style={{paddingRight: "20px", border: "none"}}><button onClick={editClicked} style={editBtnStyle}>Edit</button></td> */}
                        </tr>
                        <tr>
                            <td style={{paddingRight: "20px", border: "none"}}>300mg</td>
                            <td style={{paddingRight: "20px", border: "none"}}>10</td>
                            <td style={{paddingRight: "20px", border: "none"}}>30</td>
                            {/* <td style={{paddingRight: "20px", border: "none"}}><button style={editBtnStyle}>Edit</button></td> */}
                        </tr>
                        <tr>
                            <td style={{paddingRight: "20px", border: "none"}}>1,000mg</td>
                            <td style={{paddingRight: "20px", border: "none"}}>80</td>
                            <td style={{paddingRight: "20px", border: "none"}}>62</td>
                            {/* <td style={{paddingRight: "20px", border: "none"}}><button style={editBtnStyle}>Edit</button></td> */}
                        </tr>

                    </tbody>
                    </table>
                <div style={{backgroundColor: "#efefef", color: "#1E384D"}}>

                </div>
            </div>
      </div>
    )
}
