import React, {useEffect, useState} from "react"
import Layout from "../components/Layout"
import Table from "../components/Table"
import Calendar from "./Calendar"
import {useHistory} from "react-router-dom"
import * as router from "./vendorAPI"

export default function Vendor(props) {
  console.log(props)

    const [data, setData] = useState([])
    const [isAddPopupHidden, setAddPopupHidden] = useState(true)
    const [vendorName, setVendorName] = useState("")
    const [contactName, setContactName] = useState("")
    const [email, setEmail] = useState("")
    const [phone, setPhone] = useState("")
    const [commMethod, setCommMethod] = useState("")
    const [orderMinimum, setOrderMinimum] = useState("")
    const [deals, setDeals] = useState("")

    const outerStyle = {
        gridArea: "main", 

        backgroundColor: "rgb(246,246,247)"
      }

      const innerStyle = {
        backgroundColor: "white",
        width: "80%", 
        borderRadius: "5px",
        margin: "20px auto",
        padding: "1% 3%",
        boxShadow: `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
      }

      const calendarStyle = {
        border: "none", 
        // margin: "0 auto",
        width: "100%", 
        height: "400px",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,

        borderRadius: "5px",
        // height: "600px"
      }

      const addPopupStyle = {
        position: "absolute",
        zIndex: "10",
        backgroundColor: "white",
        padding: "10px 20px",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
        left: "45%",

    }

    const inputStyle = {
      display: "block",
      margin: "20px 0",
      padding: "5px 10px"
  }

  const submitStyle = {
    display: "block",
    margin: "20px 0"
}


    const history = useHistory()

    const columns = React.useMemo(
        () => [
          {
            Header: 'Vendor',
            accessor: 'name',
            sortDescFirst: true
          },
          {
            Header: 'ID',
            accessor: 'id',
            show: false
          },
          {
            Header: 'Contact Name',
            accessor: 'contact_name',
            show: false
          },
          {
            Header: 'Communication Method',
            accessor: 'comm_method',
            show: false
          },
          {
            Header: 'Email',
            accessor: 'email',
            show: false
          },
          {
            Header: 'Phone',
            accessor: 'phone',
            show: false
          },

          {
            Header: 'Products',
            accessor: 'products',
            sortDescFirst: true
          },

          {
            Header: 'Low Products',
            accessor: 'low_products',
            sortDescFirst: true
          },

        ],
        []
      )

      function getData() {
        router.loadVendors().then(data => {
            if (data) {            
                setData(data)
            }         
        })
      }

      function handleChange(e) {
        console.log(e.target.value)
        switch(e.target.id) {
            case "vendor_name":
                setVendorName(e.target.value)
                break;
            case "contact_name":
                setContactName(e.target.value)
                break;
            case "email":
                setEmail(e.target.value)
                break
            case "phone":
                setPhone(e.target.value)
                break
            case "comm_method":
                setCommMethod(e.target.value)
                break
            case "order_minimum":
              setOrderMinimum(e.target.value)
              break
            case "deals":
              setDeals(e.target.value)
              break
            default:
                console.log('error')
        //         setEvent(prevState => {
        //             return {
        //                 title: e.target.value,
        //                 description: prevState.description,
        //                 start: prevState.start,
        //                 end: prevState.end
        //             }
        //         })
        //         break;
        // case 
        }
        // console.log(e.target.id)

    }

    function createVendor() {

      const vendor = {
          vendorName: vendorName,
          contactName: contactName,
          email: email,
          phone: phone,
          commMethod: commMethod,
          orderMinimum: orderMinimum,
          deals: deals
      }

      save(vendor)

      // var errorFlag = false
      // Object.values(vendor).forEach(value => {
      //     if (value === null || value === "") {
      //         errorFlag = true

      //     }
      // })

      // if (errorFlag === false) {
      //     // Save in DB
      //     console.log(sale)
      //     save(vendor)
      // } else {
      //     console.log(vendor)
      //     alert(`Vendor is incomplete. Please try again`)

      // }
  }

  function save(vendor) {

      router.saveVendor(vendor).then(data => {
          if (data) {
              if (data === "Failure") {
                  alert("Failed to create vendor. Please try again")
              } else if (data === "Success") {
                  console.log("Great success")
                  setAddPopupHidden(true)
                  getData()
              }
          }
      })
  }


      useEffect(() => {
        getData()
      }, []);


    return (

        <Layout user={props.user}>
        <div style={outerStyle}>
            <div>
                <Calendar data={data}/>
            </div>
            <div style={{textAlign: "center"}}>
              <button className="btn" onClick={() => history.push("/orders")}>View All Orders</button>
            </div>   
            <div style={innerStyle}>
                  <h1>Vendors</h1>
                  <button className="circle-btn" onClick={() => setAddPopupHidden(!isAddPopupHidden)}>+</button>
       <div style={addPopupStyle} hidden={isAddPopupHidden}>
       <span className="close" style={{color: "#1E384D"}} onClick={() => setAddPopupHidden(!isAddPopupHidden)}>&times;    </span>
        <h2>Create Vendor</h2>
        <input onChange={handleChange} style={inputStyle} id="vendor_name" value={vendorName} placeholder="Vendor Name" />
        <input onChange={handleChange} style={inputStyle} id="contact_name" value={contactName} placeholder="Contact Name" />
        <input onChange={handleChange} style={inputStyle} id="email" value={email} placeholder="Email (leave blank if don't use)" />
        <input onChange={handleChange} style={inputStyle} id="phone" value={phone} placeholder="Phone (leave blank if don't use)" />
        <input onChange={handleChange} style={inputStyle} id="comm_method" value={commMethod} placeholder="Communication Method" />
        <select style={inputStyle} onChange={handleChange} id="comm_method">
          <option value="email">Email</option>
          <option value="phone">Phone</option>
          <option value="tect">Text</option>
        </select>
        <input onChange={handleChange} style={inputStyle} id="order_minimum" value={orderMinimum} placeholder="Order Minimum" />
        <input onChange={handleChange} style={inputStyle} id="deals" value={deals} placeholder="Deals" />
        {/* <input onChange={handleChange} style={inputStyle} value={eventTitle} type="text" id="event_title" placeholder="Event Title" />
         <textarea onChange={handleChange} style={inputStyle} value={eventDescription} rows="5" id="event_description" placeholder="Event Description" />

         <input onChange={handleChange} style={inputStyle} type="datetime-local" id="event_start" name="event_start" value={eventStart}/>

         <input onChange={handleChange} style={inputStyle} type="datetime-local" id="event_end" name="event_end" value={eventEnd} />

         <p style={{display: "inline"}}>All day?</p><input style={{display: "inline"}} onChange={() => setAllDay(!allDay)}type="checkbox"/>
         <select style={inputStyle} defaultValue="Choose Vendor:" onChange={handleChange} id="vendor">
             <option value="Choose Vendor:">Choose Vendor:</option>
             {data.map((vendor, id) => {
                 return (
                    <option key={id} value={vendor.id}>{vendor.name}</option>
                 )

             })}
              */}

        {/* </select> */}
         <button style={submitStyle} onClick={createVendor}>Create Vendor</button>
        </div>
                  <Table reloadData={() => getData()} user={props.user} initialSearch={props.location.state ? props.location.state.vendor : ""} columns={columns} type="vendor" data={data} search={true} popupEnabled={true} perPage={10}/>
            </div>
        </div>


          {/* <h1>Order</h1> */}

        </Layout>
    )
}
