import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import React, {useState, useEffect} from "react";
import "react-big-calendar/lib/css/react-big-calendar.css";
// import AddPopup from "./AddPopup"
import * as router from "./vendorAPI"



export default function MyCalendar(props) {
    console.log(props.user.id)
    const [eventTitle, setEventTitle] = useState("")
    const [eventDescription, setEventDescription] = useState("")
    const [vendor, setVendor] = useState("Choose Vendor:")
    const [eventStart, setEventStart] = useState("")
    const [eventEnd, setEventEnd] = useState("")
    const [allDay, setAllDay] = useState(false)
    const [isAddPopupHidden, setAddPopupHidden] = useState(true)
    const [isEventPopupHidden, setEventPopupHidden] = useState(true)
    const [clickedEvent, setClickedEvent] = useState({
        title: "Default title",
        description: "Default description",
        vendor: "Default vendor",
        start: "Default start",
        end: "Default end"
    })

    const [data, setData] = useState([])

    const inputStyle = {
        display: "block",
        margin: "20px 0",
        padding: "5px 10px"
    }

    const submitStyle = {
        display: "block",
        margin: "20px 0"
    }

    const calendarStyle = {
        backgroundColor: "white",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
        borderRadius: "5px",
        height: 400,
        // width: 800,
        margin: "20px 0", 
        padding: "10px", 
    }

    const addBtnStyle = {
        padding: "10px 15px",
        borderRadius: "50%",
        border: "none",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
        fontSize: "20px",
        color: "rgb(30,56,77)",
        backgroundColor: "white",
        fontWeight: "500",
        position: "relative",
        left: "95%",
        marginTop: "20px",
        outline: "none"
        // float: "right",
        // margin: "20px"
    }

    const addPopupStyle = {
        position: "absolute",
        zIndex: "10",
        backgroundColor: "white",
        padding: "10px 20px",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
        left: "45%",

    }

    const localizer = momentLocalizer(moment);

    function getData() {
        // console.log('getting sales')
        router.loadSales().then(data => {
            if (data) {
                setData(data)
            }         
        })
    }


    useEffect(() => {

            getData()


    }, []);

// const myEventsList = [
//     props.data.forEach(sale => {
//         sale.date = new Date(sale.date)
//     })
//   {
//     title: "Sunrise Sale",
//     start: new Date(2020, 10, 27), // 10 is novermber
//     end: new Date(2020, 11, 1), // doesn't include last day
//     allDay: true,
//     description: "20% off CBD"
//     // resource?: any,
//   },
//   {}
// ];

function createSale() {
    // setEvent
    var startDate = null
    var endDate = null
    // console.log(eventStart)
    // console.log(eventEnd)
    if (allDay && eventStart != "" && eventEnd != "") {
        startDate= eventStart.slice(0,10) + "T00:00"
        endDate = eventEnd.slice(0,10) + "T23:59"
    }
    // console.log(startDate)
    // console.log(endDate)
    const sale = {
        title: eventTitle,
        description: eventDescription,
        start_date: startDate != null ? startDate : eventStart,
        end_date: endDate != null ? endDate : eventEnd,
        all_day: allDay,
        vendor: vendor != "Choose Vendor:" ? vendor : null,
        user_id: props.user.id
    }
    // console.log(sale)

    var errorFlag = false
    Object.values(sale).forEach(value => {
        if (value === null || value === "") {
            errorFlag = true

        }
    })

    if (errorFlag === false) {
        // Save in DB
        // console.log(sale)
        save(sale)
    } else {
        // console.log(sale)
        alert(`Event is incomplete. Please try again`)

    }
}

function save(sale) {

    router.saveSale(sale).then(data => {
        if (data) {
            if (data === "Failure") {
                alert("Failed to create sale. Please try again")
            } else if (data === "Success") {
                // console.log("Great success")
                setAddPopupHidden(true)
                getData()
            }
        }
    })
}

function handleChange(e) {
    // console.log(e.target.value)
    switch(e.target.id) {
        case "event_title":
            setEventTitle(e.target.value)
            break;
        case "event_description":
            setEventDescription(e.target.value)
            break;
        case "event_start":
            setEventStart(e.target.value)
            break
        case "event_end":
            setEventEnd(e.target.value)
            break
        case "vendor":
            setVendor(e.target.value)
            break
        default:
            // console.log('error')
    //         setEvent(prevState => {
    //             return {
    //                 title: e.target.value,
    //                 description: prevState.description,
    //                 start: prevState.start,
    //                 end: prevState.end
    //             }
    //         })
    //         break;
    // case 
    }
    // console.log(e.target.id)

}

function showEvent(e) {
    // console.log(e)
    setClickedEvent({
        title: e.title,
        description: e.description,
        vendor: e.name,
        start: e.start.toString(),
        end: e.end.toString()
    })
    setEventPopupHidden(!isEventPopupHidden)
}

// console.log(data)
  return (

    <div style={{margin: "0 auto", width: "70%"}}>
          <button className="circle-btn" onClick={() => setAddPopupHidden(!isAddPopupHidden)}>+</button>
       <div style={addPopupStyle} hidden={isAddPopupHidden}>
       <span className="close" style={{color: "#1E384D"}} onClick={() => setAddPopupHidden(!isAddPopupHidden)}>&times;    </span>
        <h2>Create Event</h2>
        <input onChange={handleChange} style={inputStyle} value={eventTitle} type="text" id="event_title" placeholder="Event Title" />
         <textarea onChange={handleChange} style={inputStyle} value={eventDescription} rows="5" id="event_description" placeholder="Event Description" />

         <input onChange={handleChange} style={inputStyle} type="datetime-local" id="event_start" name="event_start" value={eventStart}/>

         <input onChange={handleChange} style={inputStyle} type="datetime-local" id="event_end" name="event_end" value={eventEnd} />

         <p style={{display: "inline"}}>All day?</p><input style={{display: "inline"}} onChange={() => setAllDay(!allDay)}type="checkbox"/>
         <select style={inputStyle} defaultValue="Choose Vendor:" onChange={handleChange} id="vendor">
             <option value="Choose Vendor:">Choose Vendor:</option>
             {props.vendorData.map((vendor, id) => {
                 return (
                    <option key={id} value={vendor.id}>{vendor.name}</option>
                 )

            })}
        </select>
         <button style={submitStyle} onClick={createSale}>Create Sale</button>
        </div>

    <div style={addPopupStyle} hidden={isEventPopupHidden}>

        <h2><strong>{clickedEvent.title}</strong></h2>
        <p><strong>{clickedEvent.description}</strong></p>
        <p><strong>Vendor: </strong>{clickedEvent.vendor}</p>
        <p><strong>Start: </strong>{clickedEvent.start}</p>
        <p><strong>End: </strong>{clickedEvent.end}</p>
    </div>
      <Calendar
        localizer={localizer}
        events={data}
        startAccessor="start"
        endAccessor="end"
        style={calendarStyle}
        // onSelectEvent={}
        tooltipAccessor="title"
        onSelectEvent={(e) => showEvent(e)}
        popup={true}
      />
    </div>
  );
}
