import "../styles/Popup.css"
import {useState, useEffect} from "react"
import {useHistory} from "react-router-dom"
import * as router from "./productAPI"

export default function ProductPopup(props) {

    const [btnText, setBtnText] = useState("Edit")
    const [isEditing, setIsEditing] = useState(false)
    const [editColor, setEditColor] = useState("#76c32d")
    const [dataToUpdate, setDataToUpdate] = useState([])
    const [data, setData] = useState([])
    const [isLoading, setLoading] = useState(false)

    const history = useHistory()

    const imageStyle = {
        width: "15%",
        margin: "20px 0",
        display: "inline-block",
        float: "left"
    }

    const titleStyle = {
        fontSize: "30px"
    }

    function getData() {
        setLoading(true)
        router.loadProduct(props.values.id).then(data => {
            if (data) {
                setData(data)
                setLoading(false)      
            }
        });
      }

    function editBtnClicked(e) {
        if (btnText === "Edit") {
            setBtnText("Save")
        } else {
            // Save data
            const updateDict = {}
            dataToUpdate.forEach(item => {
                updateDict[item.id] = item.quantity
            })

            if (dataToUpdate.length > 0) {
                router.updateProduct(props.values.id, updateDict).then(data => {
                    if (data === "Success") {
                        setBtnText("Edit")
                        getData()
                    } else {
                        setBtnText("Edit")
                    }
                })
            } else {
                setBtnText("Edit")
            } 
        }
        setIsEditing(!isEditing)
        setEditColor(editColor === "#26B1FF" ? "#76c32d" : "#26B1FF")
    }

    function editInput(e) {
        if (e.target.value !== e.target.placeholder) {
            // Quantity value has changed for this variant
            const newData = {
                "id": e.target.id,
                "quantity": e.target.value
            }
            setDataToUpdate(oldArray => [...oldArray, newData])
        }
    }

    useEffect(() => {
        getData()
    }, []);

    if (isLoading) {
        return <div className="loader"></div>;
    }

    return (
        <div>
            <div className="top-content">
                <span className="close" onClick={() => props.toggle()}>&times;    </span>
                {data[0]  ? <img style={imageStyle} src={data[0].image} alt="product image"/> : <p></p>}

                <h2 style={titleStyle}>{data[0] ? data[0].product_title : "Default"}</h2>

                <p>Shopify ID: {data[0] ? data[0].shopify_id : "Default"}</p>
                <button onClick={editBtnClicked} className="edit-btn" style={{backgroundColor: editColor}}>{btnText}</button>
                <button onClick={() => history.push("/vendors", {vendor: props.values.vendor})}className="edit-btn" style={{color: "#76c32d"}}>Order</button>
            </div>

            <table style={{textAlign: "center"}}>
                <thead >
                    <tr>
                        <th className="table-header">Variant</th>
                        <th className="table-header">Price</th>
                        <th className="table-header">Quantity</th>
                    </tr>
                </thead>
                <tbody>
                {data.map((item, index) => {
                    return (  
                        <tr key={index}>
                            <td className="table-cell">{item.variant_title}</td>
                            <td className="table-cell">${item.price}</td>
                            {isEditing 
                                ? 
                                <td className="table-cell">
                                    <input className="table-cell" type="text" onChange={editInput} id={item.variant_id} placeholder={item.quantity} />
                                </td>
                                :
                                <td className="table-cell">
                                    {item.quantity}
                                </td>
                                }
                        </tr>
                    ) 
                })}   
                </tbody>
            </table>            
        </div>
    )
}
