import "../styles/Popup.css"
import {useState, useEffect, useRef, useMemo} from "react"
import MessageOption from "./MessageOption"
import Table from "../components/Table"
import * as router from "./vendorAPI"


export default function VendorPopup(props) {
    console.log(props)

    const variantSelect = useRef()
    const productSelect = useRef()
    const quantityInput = useRef()

    const [percentage, setPercentage] = useState(0)

    const [btnText, setBtnText] = useState("Order")
    const [isOrdering, setOrdering] = useState(false)
    const [isLoading, setLoading] = useState(true)
    const [editColor, setEditColor] = useState("#76c32d")
    const [isOrderItemsHidden, setOrderItemsHidden] = useState(true)
    const [isOrderOptionsHidden, setOrderOptionsHidden] = useState(true)
    const [dataToUpdate, setDataToUpdate] = useState([])
    const [variantsToUpdate, setVariantsToUpdate] = useState([])
    const [data, setData] = useState([])

    const [variantData, setVariantData] = useState([])
    const [isAddBtnHidden, setAddBtnHidden] = useState(true)
    const [isQuantityHidden, setQuantityHidden] = useState(true)
    const [quantity, setQuantity] = useState(0)
    const [isProductSelectHidden, setProductSelectHidden] = useState(true)
    const [isVariantSelectHidden, setVariantSelectHidden] = useState(true)
    const [isExtraOptionHidden, setExtraOptionHidden] = useState(true)
    const [extraOptionText, setExtraOptionText] = useState("")
    const [productSelected, setProductSelected] = useState({title: "", id: ""})
    const [variantSelected, setVariantSelected] = useState({title: "", id: "", cost: 0})
    const [nextPage, setNextPage] = useState(false)
    const [reviewOrder, setReviewOrder] = useState(false)
    const [orderState, setOrderState] = useState(null)
    const commMethodPlain = props.data.row.values.comm_method
    var commMethod = commMethodPlain.includes(',') ? commMethodPlain.split(',')[0] : commMethodPlain
    console.log(commMethod)

    const formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
        minimumFractionDigits: 0
      })

    const phone = props.values.phone ? `${props.values.phone.slice(0,3)}-${props.values.phone.slice(3,6)}-${props.values.phone.slice(6,)}` : ""

    console.log(dataToUpdate)
    console.log(variantData)
    console.log(props)
    // console.log(data)

    const orderItemsStyle = {
        color: "black", 
        margin: "20px 0", 
        padding: "10px 20px", 
        backgroundColor: "#efefef", 
        textAlign: "center"
    }


    const columns = useMemo(
        () => [
        {
            Header: 'ID',
            accessor: 'id', // Product ID
            sortDescFirst: true
            },
          {
            Header: 'Product',
            accessor: 'title',
            sortDescFirst: true
          },
          {
            Header: 'Variant',
            accessor: 'variant',
            sortDescFirst: true,
            show: true
          },
          {
            Header: 'Quantity',
            accessor: 'quantity',
            sortDescFirst: true,
          },
          {
            Header: 'Recent Purchases',
            accessor: 'purchases',
            sortDescFirst: true,
          },
          {
            Header: 'Stock Level',
            accessor: 'stockLevel',
            disableSortBy: true
          },
          {
            Header: 'Recent Order',
            accessor: 'recentOrder',
            disableSortBy: true
          },
        ],
        []
      )


      const reviewColumns = useMemo(
        () => [

          {
            Header: 'Product',
            accessor: 'title',
            sortDescFirst: true
          },
          {
            Header: 'Variant',
            accessor: 'variant',
            sortDescFirst: true,
            show: true
          },
          {
            Header: 'Variant ID',
            accessor: 'variantId',
            show: false
          },
          {
            Header: 'Order Quantity',
            accessor: 'orderQuantity',
            sortDescFirst: true,
          },
          {
            Header: 'Cost/unit',
            accessor: 'cost',
            sortDescFirst: true,
          },
          {
            Header: 'Subtotal',
            accessor: 'subtotal',
            sortDescFirst: true,
          },
        ],
        []
      )



                    //             <th></th>
                    //             <th>Variant</th>
                    //             <th>Current Quantity</th>
                    //             <th>Recent Purchases</th>
                    //             <th>Stock Level</th>
                    //             <th>Recent Order</th>
                    //             <th>Cost</th>
                    //             <th>Order Quantity</th>
                    //         </tr>
                    //     </thead>
                    //     <tbody>
                    //         {variantData.map((variant, index) => {

                    //                 return (
                    //                     <tr key={index}>
                    //                         <td>{variant.product}</td>
                    //                         <td>{variant.variant}</td>
                    //                         <td>{variant.quantity}</td>
                    //                         <td>{variant.purchases}</td>
                    //                         <td>{variant.stockLevel}</td>
                    //                         <td>{variant.recentOrder}</td>
                    //                         <td>{variant.cost}</td>
                    //                         <input type="text" placeholder="0"/>
                    //                     </tr>
                    //        




    function addColumns(columns) {

        const newColumns = columns
        newColumns.push(
            {
                Header: 'Variant',
                accessor: 'variant',
                sortDescFirst: true 
            },
            {
                Header: 'Cost',
                accessor: 'cost',
                sortDescFirst: true
            }
        )
        return newColumns

    }




    //   const newColumns = useMemo(
    //     () => [
    //     {
    //         Header: 'Product',
    //         accessor: 'product', // Product ID
    //         sortDescFirst: true
    //         },
    //       {
    //         Header: 'Variant',
    //         accessor: 'variant',
    //         sortDescFirst: true
    //       },
    //       {
    //         Header: 'Current Quantity',
    //         accessor: 'quantity',
    //         sortDescFirst: true,
    //       },
    //       {
    //         Header: 'Recent Purchases',
    //         accessor: 'purchases',
    //         sortDescFirst: true,
    //       },
    //       {
    //         Header: 'Stock Level',
    //         accessor: 'stockLevel',
    //         sortDescFirst: true,
    //       },
    //       {
    //         Header: 'Recent Order',
    //         accessor: 'recentOrder',
    //         sortDescFirst: true,
    //       },
    //     ],
    //     []
    //   )







    function getData() {
        setLoading(true)
        router.loadVendor(props.values.id).then(data => {
            if (data) {
                // console.log(data)
                setData(data)
                setLoading(false)
            } else {
                console.log("Error getting product data")
            }
        });
      }

      useEffect(() => {
        getData()
    }, []);

    function getVariants(e, hasVariants) {
        router.loadVariants(e.target.value).then(data => {
          console.log('result of getProduct')
          if (data) {
            //   console.log(res.data)

              if (hasVariants) {
                setVariantData(data)
                setVariantSelectHidden(false)
              } else {
                setVariantSelected({
                    id: data[0].id,
                    title: data[0].text,
                    cost: data[0].cost
                })
                setQuantityHidden(false)
                //   console.log(res.data)
              }

            //   setLoading(false)      
          } else {
              console.log("Error getting product data")
          }
      });
    }

    function orderClicked(btnIndex) {
        const phone = `${props.values.phone.slice(0,3)}-${props.values.phone.slice(3,6)}-${props.values.phone.slice(6,)}`
        const orderSteps = commMethodPlain.includes("call") ? 4 : 3
        console.log(orderSteps)
        if (!isOrdering) {
            // Start Order - change to add products
            setOrdering(true)
            setBtnText("Next")
            setOrderItemsHidden(false)
            setOrderState("Choose Products")
            setPercentage(100/orderSteps)

        } else if (orderState === "Choose Products") {
            // Going to next page of ordering after added products - choose variants and quantities
            setPercentage(100/orderSteps * 2) // update progress bar
            setNextPage(true)
            setOrderState("Choose Variants/Qty")

            router.loadOrderProducts(dataToUpdate).then(data => {
                  if (data) {
                    setVariantData(data)
                    setDataToUpdate([]) // empty dataToUpdate from any previous orders
                    setBtnText("Review")
                  }
            })

        } else if (orderState === "Choose Variants/Qty") {
            // Going to next page to review order and submit or use communication method
            setNextPage(false)
            setReviewOrder(true)
            setPercentage(100/orderSteps * 3) // update progress bar

            if (commMethodPlain.includes(',')) {
                // Multiple vendors
                setExtraOptionHidden(false)
                setExtraOptionText(`${capitalizeFirstLetter(commMethodPlain.split(',')[1])} ${commMethodPlain.split(',')[1] === 'call' ? phone : ""}`)
                setBtnText(`${capitalizeFirstLetter(commMethod)} ${commMethod === 'call' ? phone : ""}`)
                setOrderState("Multiple Vendors")


            } else {
                // Only 1 vendor (as it is for the vast majority)
                if (commMethodPlain.includes("website")) {        
                    setBtnText("Order on website")
                    setOrderState("Website Order")
                } else if (commMethodPlain.includes('call')) {
                    setBtnText(`Call ${phone}`)
                    setOrderState("Calling Order")         
                } else {
                    setBtnText("Submit")
                    setOrderState("Review Order")
                }
            }

            setOrderOptionsHidden(false)
            // setOrderState("Review Order")


        } else if (orderState === "Website Order") {
           window.open("https://amazon.com",'_blank') // NEED TO CHANGE
           setBtnText("Submit")
           setOrderState("Review Order")
           setPercentage(100/orderSteps * 4)
        } else if (orderState === "Calling Order") {
            setPercentage(100/orderSteps * 4)
            setBtnText("Submit")
            setOrderState("Review Order")
        } else if (orderState === "Multiple Vendors") {
            // One of the 2 buttons was clicked
            const method = commMethodPlain.split(',')[btnText]
            console.log(method)
            if (method === "call") {
                // Clicked on the Call button so now need to submit the order
                setPercentage(100/orderSteps * 4)
                setBtnText("Submit")
                setOrderState("Review Order")
            } else if (method === "website") {
                // Clicked on Website so now need to submit the order
                window.open("https://amazon.com",'_blank') // NEED TO CHANGE
                setBtnText("Submit")
                setOrderState("Review Order")
                setPercentage(100/orderSteps * 4)
            } else {
                // Clicked on Email or Text so need to send the message which will then save the order
                console.log(btnText)
                sendMessage(btnText)
                setOrderState(null)
            }
        }  else if (orderState === "Review Order") {
            // Submit - save order
            commMethodPlain.includes(',') ? save() : sendMessage() 
            setOrderState(null)
        } 
        // else {
        //     // this only happens when have multiple vendors
        //     console.log('this was called')
        //     sendMessage(btnIndex)
        //     setOrderState(null)
        // }
    }

    function backClicked() {
        console.log(orderState)
        const phone = `${props.values.phone.slice(0,3)}-${props.values.phone.slice(3,6)}-${props.values.phone.slice(6,)}`
        const orderSteps = commMethod === "text" || commMethod === "email" ? 4 : 5
        if (orderState === "Choose Products") {
            console.log('u know it dawg')
            setOrdering(false)
            setBtnText("Order")
            setOrderItemsHidden(true)
            setOrderState(null)
            setPercentage(0)
        } else if (orderState === "Choose Variants/Qty") {
            setNextPage(false)
            setOrderState("Choose Products")
            setBtnText("Next")
            setPercentage(100/orderSteps)
            // newColumns = addColumns(columns)
            setVariantData([])
            setDataToUpdate([])
            setVariantsToUpdate([])

            // router.loadOrderProducts(dataToUpdate).then(data => {
            //       console.log('result of getProduct')
            //       if (data) {
            //         console.log('yuheee')
            //         console.log(data)
            //         setVariantData(data)
            //         setDataToUpdate([])
            //         setBtnText("Review")
            //       }
            // })
        } else if (orderState === "Review Order") {
            setNextPage(true)
            setReviewOrder(false)
            setBtnText("Review")
            setOrderOptionsHidden(true)
            setOrderState("Choose Variants/Qty")
            setPercentage(100/orderSteps * 2)
        } else if (orderState === "Calling") {
            console.log('we hee right?')
            setBtnText(`Call: ${phone}`)
            setOrderState("Review Order")
            setPercentage(100/orderSteps * 3)


            setNextPage(false)
            setReviewOrder(true)

            setOrderOptionsHidden(false)




        }

    }



    function selectChanged(e) {

        // Get the text and remove the stockLevel at the end

        // console.log(text.slice(0,-1).join(" "))
        // console.log(e.target.label)
        // console.log(e.target.text)
        if (e.target.id === "productFilter") {

            const text = e.target[e.target.selectedIndex].label.split(" ").slice(0,-1).join(" ")

            setProductSelected({
                id: e.target.value,
                title: text
            })
            // Check to see if has variants
            var hasVariants = false
            if (e.target[e.target.selectedIndex].text != 1) {
                hasVariants = true
            } 

            getVariants(e, hasVariants)



        } else {

            const text = e.target[e.target.selectedIndex].text
            setVariantSelected({
