import {useState, useEffect} from "react"
import {useHistory} from "react-router"
import * as router from "./homeAPI"

export default function CallToAction(props) {

  /*

  Displays the number of products that are running low and sends to 
  products page with low product data on click

  */

  const [data, setData]= useState([])
  const [productsRunningLow, setProductsRunningLow] = useState(0)

  const history = useHistory();

  const headingStyle = {
    color: "#1E384D",
    margin: "5%",
    fontSize: "1.5rem"
  }

  const productsLowNumStyle = {
    color: "red",
    fontSize: "30px",
    margin: "0"
  }

  const smallText = {
    fontSize: "12px",
  }

  // function getData() {
  //   router.loadLowProducts().then(data => {
  //     if (data) {
  //       setData(data)
  //       setProductsRunningLow(data.length)
  //     }

  //   })
  // }

  // useEffect(() => {
  //   getData()
  //   // eslint-disable-next-line react-hooks/exhaustive-deps
  //   }, []);

  return (
      <div style={{ width: "100%",}}>
        <div className="cta" onClick={() => history.push("/products", {products: props.data})}>
          <h2 style={headingStyle}>Products Running Low</h2>
          <strong><p style={productsLowNumStyle}>{props.data.length}</p></strong>
          <p style={smallText}>View Products</p>
        </div>
      </div>
    )
}
