import {useState, useEffect} from "react"
import {useHistory} from "react-router"
import * as router from "./homeAPI"

export default function CallToAction() {
    const [data, setData]= useState([])
    const [productsRunningLow, setProductsRunningLow] = useState(0)

    const history = useHistory();

    function getLowProducts() {
      router.loadLowProducts().then(data => {
        if (data) {
          setData(data)
          setProductsRunningLow(data.length)
        }

      })
    }

    function handleClick() {
        console.log('clickedd')
        history.push("/products", {products: data})
        // return <Redirect to="/inventory"/>
    }

    useEffect(() => {
      getLowProducts()
      }, []);


    const ctaStyle = {
        padding: "10px 25px",
        // width: "20%",
        margin: "20px auto 0",
        lineHeight: "1",
        // margin: "0 500px",

        // position: 'absolute', 
        // top: "10px", 
        // right: "35%",
        backgroundColor: "white",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
        borderRadius: "5px",



      }
      // 0 2.8px 2.2px rgba(0, 0, 0, 0.034),
      //   0 6.7px 5.3px rgba(0, 0, 0, 0.048),
      //   0 12.5px 10px rgba(0, 0, 0, 0.06),
      //   0 22.3px 17.9px rgba(0, 0, 0, 0.072),
      //   0 41.8px 33.4px rgba(0, 0, 0, 0.086),
      //   0 100px 80px rgba(0, 0, 0, 0.12)

      const headingStyle = {
        color: "#1E384D",
        margin: "5%",
        fontSize: "1.5rem"
      }

      // .box {




        // min-height: 200px;
        // width: 50vw;
      //   margin: 100px auto;
      //   background: white;

      // }


      const productsLowNumStyle = {
        color: "red",
        fontSize: "30px",
        margin: "0"
      }

      const smallText = {
        fontSize: "12px",

      }

    return (

                <div style={ctaStyle} onClick={handleClick}>
                  <h2 style={headingStyle}>Products Running Low</h2>
                  <strong><p style={productsLowNumStyle}>{productsRunningLow}</p></strong>
                  <p style={smallText}>View Products</p>
                </div>

    )
}
