import { ResponsiveContainer, Legend, LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip } from 'recharts';

export default function SalesChart(props) {

    const formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
        minimumFractionDigits: 0
    })


    const CustomTooltip = ({ active, payload, label }) => {        

        if (active) {
            return (
            <div className="custom-tooltip">
                <strong><p className="label">{payload[0] ? payload[0].payload.date : null}</p></strong>
                <p className="label" style={{color: "#8884d8" }}>{payload[0].payload.shopify  ? `Shopify: ${formatter.format(payload[0].payload.shopify)}` : null}</p>
                <p className="label" style={{color: "red" }}>{payload[0].payload.woo ? `Woo: ${formatter.format(payload[0].payload.woo)}` : null}</p>
            </div>
            );
        }

        return null;
        };


    return (
        <ResponsiveContainer width="100%" height="80">
            <LineChart data={props.data} >
                <Legend verticalAlign="top" height={36}/>
                <Line type="monotone" dataKey="shopify" stroke="#8884d8" />
                <Line type="monotone" dataKey="woo" stroke="red" />
                <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip content={<CustomTooltip />}/>
            </LineChart>
        </ResponsiveContainer>
    )
}
