    width: "45%", 
    backgroundColor: "white",
    boxShadow:
    `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
    borderRadius: "5px",
    padding: "2%"

  }

  const tableStyle = {
    margin: "20px 0", 
    padding: "10px 40px", 
    backgroundColor: "white",
    boxShadow:
    `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
    borderRadius: "5px",
  }



  function getPurchases(filter) {
    setLoading(true)
    router.loadPurchases(filter, startDate, endDate).then(data => {
      if (data) {
        setSalesData(data.salesData)
        setCogsData(data.cogsData)

      }
      setLoading(false)
