export default function MessageOption(props) {

    var toField = ""
    var subject = null

    if (props.type === "Email") {
        toField = `mailto:${props.to}`
        subject = "?subject=Burman's%20Health%20Shop%20Order"
    } else if (props.type === "Text") {
        toField = `sms:+1${props.to}`
    }    

    return (
        <button>
            <a target="_blank" href={`${toField}${subject ? subject : ""}&body=Hey ${props.contact_name}! %0D%0A%0D%0AHope all is well. Can you please fulfill this order for us?%0D%0A%0D%0A----------------------------------------%0D%0A
                ${props.dataToUpdate.map((orderItem, index) => {
                    console.log(orderItem)
                    var item = orderItem.productTitle 
                    if (orderItem.variantTitle) {
                        item += " - " + orderItem.variantTitle
                    }
                    return (

                    orderItem === '\n'? '%0D%0A' : `${item}: ${orderItem.quantity}`
                        // document.write("\n")


                    )
                }).join("\n")}%0D%0A%0D%0A----------------------------------------%0D%0A%0D%0AThanks!%0D%0A%0D%0A-%20Ted`}>
               {`${props.type}: ${props.to}`}
            </a>
        </button>
    )

}
