import React, {useEffect, useState} from "react"
import Layout from "../components/Layout"
import Table from "../components/Table"
import DownloadBtn from "../components/DownloadBtn"
import Calendar from "./Calendar"
import * as router from "./vendorAPI"

export default function Vendor(props) {

  const [data, setData] = useState([])
  const [isAddPopupHidden, setAddPopupHidden] = useState(true)
  const [isLoading, setLoading] = useState(false)
  const [vendorName, setVendorName] = useState("")
  const [contactName, setContactName] = useState("")
  const [email, setEmail] = useState("")
  const [phone, setPhone] = useState("")
  const [commMethod, setCommMethod] = useState("")
  const [orderMinimum, setOrderMinimum] = useState("")
  const [deals, setDeals] = useState("")
  const [isCalendarHidden, setCalendarHidden] = useState(true)

  const outerStyle = {
    gridArea: "main", 
    backgroundColor: "rgb(246,246,247)"
  }

  const innerStyle = {
    backgroundColor: "white",
    width: "80%", 
    borderRadius: "5px",
    margin: "20px auto",
    padding: "1% 3%",
    boxShadow: `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
  }

  const inputStyle = {
    display: "block",
    margin: "20px 0",
    padding: "10px 20px",
    fontSize: "16px",
    width: "80%",
    border: "none",
    borderBottom: "1px solid #76c32d"
  }    

  const columns = React.useMemo(
    () => [
      {
        Header: 'Vendor',
        accessor: 'name',
        sortDescFirst: true
      },
      {
        Header: 'ID',
        accessor: 'id',
        show: false
      },
      {
        Header: 'Contact Name',
        accessor: 'contact_name',
        show: false
      },
      {
        Header: 'Communication Method',
        accessor: 'comm_method',
        show: false
      },
      {
        Header: 'Email',
        accessor: 'email',
        show: false
      },
      {
        Header: 'Phone',
        accessor: 'phone',
        show: false
      },

      {
        Header: 'Products',
        accessor: 'products',
        sortDescFirst: true
      },

      {
        Header: 'Low Products',
        accessor: 'low_products',
        sortDescFirst: true
      },

    ],
    []
  )

  function getData() {    
    setLoading(true)
    router.loadVendors().then(data => {
      if (data) {
        setData(data)
      }     
    })
    setLoading(false)     
  }

  useEffect(() => {
    getData()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  function handleChange(e) {
    switch(e.target.id) {
      case "vendor_name":
          setVendorName(e.target.value)
          break;
      case "contact_name":
          setContactName(e.target.value)
          break;
      case "email":
          setEmail(e.target.value)
          break
      case "phone":
          setPhone(e.target.value)
          break
      case "comm_method":
          setCommMethod(e.target.value)
          break
      case "order_minimum":
        setOrderMinimum(e.target.value)
        break
      case "deals":
        setDeals(e.target.value)
        break
      default:
        break
      }       
    }

    function createVendor() {

      const vendor = {
          vendorName: vendorName,
          contactName: contactName,
          email: email,
          phone: phone,
          commMethod: commMethod,
          orderMinimum: orderMinimum,
          deals: deals
      }

      if (commMethod === "default") {
        alert(`Change Communication Method`)
      } else {
        save(vendor)
      }
  }

  function save(vendor) {
    router.saveVendor(vendor).then(data => {
      if (data) {
        if (data === "Failure") {
          alert("Failed to create vendor. Please try again")
        } else if (data === "Success") {
          console.log("Great success")
          setAddPopupHidden(true)
          getData()
        }
      }
    })
  }

  if (isLoading) {
    return <div className="loader"></div>;
  }

  return (
    <Layout user={props.user}>
      <div style={outerStyle}>
        <div hidden={isCalendarHidden}>
            <Calendar vendorData={data} user={props.user}/>
        </div>
        <div style={{textAlign: "center", margin: "20px 0"}}>
          <button className="btn" onClick={() => setCalendarHidden(!isCalendarHidden)}>View Sales Calendar</button>
        </div>   
        <div style={innerStyle}>
          <h1>Vendors</h1>
          <DownloadBtn data={data} type="vendors" />
          <button className="circle-btn" style={{margin: "0", left: "80%", bottom: "85px"}} onClick={() => setAddPopupHidden(!isAddPopupHidden)}>+</button>
          <div className="add-popup" hidden={isAddPopupHidden}>
            <span className="close" style={{color: "#1E384D"}} onClick={() => setAddPopupHidden(!isAddPopupHidden)}>&times;    </span>
            <h2>Create Vendor</h2>
            <input onChange={handleChange} type="text" style={inputStyle} id="vendor_name" value={vendorName} placeholder="Vendor Name" />
            <input onChange={handleChange} type="text" style={inputStyle} id="contact_name" value={contactName} placeholder="Contact Name" />
            <input onChange={handleChange} type="text" style={inputStyle} id="email" value={email} placeholder="Email (leave blank if don't use)" />
            <input onChange={handleChange} type="text" style={inputStyle} id="phone" value={phone} placeholder="Phone (leave blank if don't use)" />
            <select style={inputStyle} defaultValue="default" onChange={handleChange} id="comm_method">
              <option value="default">Choose Communciation Method:</option>
              <option value="email">Email</option>
              <option value="phone">Phone</option>
              <option value="text">Text</option>
            </select>
            <input onChange={handleChange} type="text" style={inputStyle} id="order_minimum" value={orderMinimum} placeholder="Order Minimum" />
            <input onChange={handleChange} type="text" style={inputStyle} id="deals" value={deals} placeholder="Deals" />
            <button className="btn-2" style={{margin: "0"}} onClick={createVendor}>Create Vendor</button>
          </div>
          <Table reloadData={() => getData()} user={props.user} initialSearch={props.location.state ? props.location.state.vendor : null} columns={columns} type="vendor" data={data} search={true} popupEnabled={true} perPage={10}/>
        </div>
      </div>  
    </Layout>
  )
}
