import React from "react"
import { useTable, useAsyncDebounce, useGlobalFilter } from "react-table"



export default function Table() {

    // Define a default UI for filtering
function GlobalFilter({
    preGlobalFilteredRows,
    globalFilter,
    setGlobalFilter,
  }) {
    const count = preGlobalFilteredRows.length
    const [value, setValue] = React.useState(globalFilter)
    const onChange = useAsyncDebounce(value => {
      setGlobalFilter(value || undefined)
    }, 200)

    return (
      <span>
        Search:{' '}
        <input
          value={value || ""}
          onChange={e => {
            setValue(e.target.value);
            onChange(e.target.value);
          }}
          placeholder={`${count} records...`}
          style={{
            fontSize: '1.1rem',
            border: '0',
          }}
        />
      </span>
    )
  }

    const data = React.useMemo(
        () => [
          {
            productName: 'Delta 8 Disposable Vape Cart',
            quantity: 20,
            variants: 3,
            stockLevel: "Low"
          },
          {
            productName: 'Bubba Kush',
            quantity: 100,
            variants: 2,
            stockLevel: "High"
          },
          {
            productName: 'Kanna Bliss',
            quantity: 52,
            variants: 0,
            stockLevel: "Medium"
          },
          {
            productName: 'Delta 8 Disposable Vape Cart',
            quantity: 20,
            variants: 3,
            stockLevel: "Low"
          },
          {
            productName: 'Bubba Kush',
            quantity: 100,
            variants: 2,
            stockLevel: "High"
          },
          {
            productName: 'Kanna Bliss',
            quantity: 52,
            variants: 0,
            stockLevel: "Medium"
          },
        ],
        []
      )

    const columns = React.useMemo(
        () => [
          {
            Header: 'Product Name',
            accessor: 'productName', // accessor is the "key" in the data
          },
          {
            Header: 'Quantity',
            accessor: 'quantity',
          },
          {
            Header: 'Variants',
            accessor: 'variants',
          },
          {
            Header: 'Stock Level',
            accessor: 'stockLevel',
          },
        ],
        []
      )



    const tableInstance = useTable({ columns, data, filterTypes }, useGlobalFilter)

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        state,
        prepareRow,
        preGlobalFilteredRows,
        setGlobalFilter,
      } = tableInstance


      function fuzzyTextFilterFn(rows, id, filterValue) {
        return matchSorter(rows, filterValue, { keys: [row => row.values[id]] })
      }

      // Let the table remove the filter if the string is empty
      fuzzyTextFilterFn.autoRemove = val => !val


      const filterTypes = React.useMemo(
        () => ({
          // Add a new fuzzyTextFilterFn filter type.
          fuzzyText: fuzzyTextFilterFn,
          // Or, override the default text filter to use
          // "startWith"
          text: (rows, id, filterValue) => {
            return rows.filter(row => {
              const rowValue = row.values[id]
              return rowValue !== undefined
                ? String(rowValue)
                    .toLowerCase()
                    .startsWith(String(filterValue).toLowerCase())
                : true
            })
          },
        }),
        []
      )


      return (
          // apply the table props
        <div class="inventory-table">
             <GlobalFilter
                preGlobalFilteredRows={preGlobalFilteredRows}
                globalFilter={state.globalFilter}
                setGlobalFilter={setGlobalFilter}
              />
        <table {...getTableProps()}>

          <thead>
            {// Loop over the header rows
            headerGroups.map(headerGroup => (
              // Apply the header row props
              <tr {...headerGroup.getHeaderGroupProps()}>
                {// Loop over the headers in each row
                headerGroup.headers.map(column => (
                  // Apply the header cell props
                  <th {...column.getHeaderProps()}>
                    {// Render the header
                    column.render('Header')}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          {/* Apply the table body props */}
          <tbody {...getTableBodyProps()}>
            {// Loop over the table rows
            rows.map(row => {
              // Prepare the row for display
              prepareRow(row)
              return (
                // Apply the row props
                <tr {...row.getRowProps()}>
                  {// Loop over the rows cells
                  row.cells.map(cell => {
                    // Apply the cell props
                    return (
                      <td {...cell.getCellProps()}>
                        {// Render the cell contents
                        cell.render('Cell')}
                      </td>
                    )
                  })}
                </tr>
              )
            })}
          </tbody>
        </table>
        </div>
      )

}
