import React, {useState} from "react"
import Header from "./Header"
import Sidenav from "./Sidenav"
import MenuIcon from "../images/menu.png"

export default function Layout({ children, props }) {
    console.log('layout props:')
    console.log(props)


    const [sideNavClass, setSideNavClass] = useState("sidenav");

    function menuClicked(e) {
        // e.target.style.transform = "translateX(0)"
        // Sidenav.active
        if (sideNavClass == "sidenav") {
            setSideNavClass("sidenav active")
        } else {
            setSideNavClass("sidenav")
        }

    }

    return (
        <div style={{backgroundColor: "white", height: "100vh"}}>
             <div id="toggle-icon" onClick={menuClicked} className="sidenav__close-icon">
                <img
                    id="toggle-img"
                    className="menu-icon"
                    src={MenuIcon}
                    alt="menu icon"
                />
                </div>
            {/* <div style={gridContainer} className="grid-container">
                <Header />
                <Sidenav class={sideNavClass}/>
                <Main />
            </div> */}
            <div className="grid-container">
                <Header user={props.user}/>
                <Sidenav user={props.user} class={sideNavClass}/>
                {/* <Header user={props.user}/>
                <Sidenav user={props.user} class={sideNavClass}/> */}
                {children}
            </div>
        </div>

    )
}
