import "../styles/Popup.css"
import {useState, useEffect, useRef} from "react"
import MessageOption from "./MessageOption"
import * as router from "./vendorAPI"


export default function VendorPopup(props) {

    console.log(props)

    const variantSelect = useRef()
    const productSelect = useRef()
    const quantityInput = useRef()

    const [btnText, setBtnText] = useState("Order")
    const [isOrdering, setOrdering] = useState(false)
    const [isLoading, setLoading] = useState(true)
    const [editColor, setEditColor] = useState("#76c32d")
    const [isOrderItemsHidden, setOrderItemsHidden] = useState(true)
    const [isOrderOptionsHidden, setOrderOptionsHidden] = useState(true)
    const [dataToUpdate, setDataToUpdate] = useState([])
    const [data, setData] = useState([])

    const [variantData, setVariantData] = useState([])
    const [isAddBtnHidden, setAddBtnHidden] = useState(true)
    const [isQuantityHidden, setQuantityHidden] = useState(true)
    const [quantity, setQuantity] = useState(0)
    const [isProductSelectHidden, setProductSelectHidden] = useState(true)
    const [isVariantSelectHidden, setVariantSelectHidden] = useState(true)
    const [productSelected, setProductSelected] = useState({title: "", id: ""})
    const [variantSelected, setVariantSelected] = useState({title: "", id: "", cost: 0})

    const comm_method = props.data.row.values.comm_method

    const orderItemsStyle = {
        color: "black", 
        margin: "20px 0", 
        padding: "10px 20px", 
        backgroundColor: "#efefef", 
        textAlign: "center"
    }


    function getVendor() {
        setLoading(true)
        router.loadVendor(props.values.id).then(data => {
            if (data) {
                setData(data)
                setLoading(false)
            } else {
                console.log("Error getting product data")
            }
        });
      }

      useEffect(() => {
        getVendor()
    }, []);

    function getVariants(e, hasVariants) {
        router.loadVariants(e.target.value).then(data => {
          console.log('result of getProduct')
          if (data) {
            //   console.log(res.data)

              if (hasVariants) {
                setVariantData(data)
                setVariantSelectHidden(false)
              } else {
                setVariantSelected({
                    id: data[0].id,
                    title: data[0].text,
                    cost: data[0].cost
                })
                setQuantityHidden(false)
                //   console.log(res.data)
              }

            //   setLoading(false)      
          } else {
              console.log("Error getting product data")
          }
      });
    }

    function orderClicked() {
        if (btnText === "Order") {
            // Start Order - add products
            setOrdering(true)
            setBtnText("Next")
            setOrderItemsHidden(false)

        } else if (btnText === "Next") {
            // Going to next page of ordering after added products
            setOrderOptionsHidden(false)
            setEditColor("#26B1FF")
            setBtnText("Submit")

        } else {
            // Submit - save order
            save()
        }
    }

    function selectChanged(e) {

        // Get the text and remove the stockLevel at the end

        // console.log(text.slice(0,-1).join(" "))
        // console.log(e.target.label)
        // console.log(e.target.text)
        if (e.target.id === "productFilter") {

            const text = e.target[e.target.selectedIndex].label.split(" ").slice(0,-1).join(" ")

            setProductSelected({
                id: e.target.value,
                title: text
            })
            // Check to see if has variants
            var hasVariants = false
            if (e.target[e.target.selectedIndex].text != 1) {
                hasVariants = true
            } 

            getVariants(e, hasVariants)



        } else {

            const text = e.target[e.target.selectedIndex].text
            setVariantSelected({
                id: e.target.value.split(',')[0],
                title: text,
                cost: e.target.value.split(',')[1]
            })
            setQuantityHidden(false)
        }
    }

    function save() {
        const order = {
            vendor: props.data.row.values.id,
            subtotal: 0,
            status: "submitted"
        }

        const orderItems = []
        dataToUpdate.forEach(item => {
            console.log(item.subtotal)
            if (item != "\n") {
                order.subtotal += item.subtotal
                orderItems.push({
                    variant: item.variantId,
                    subtotal: item.subtotal, // this is the subtotal including multiple qtys
                    quantity: item.quantity
                })
            }
        })

        if (dataToUpdate.length > 0) {
            router.saveOrder(order, orderItems, props.user.id).then(data => {
                console.log(data)
            if (data === "Success") {
                props.toggle()
            } else {
                console.log('error saving')
            }
         })
        } 
    }


    function handleChange(e) {
        setQuantity(e.target.value)
        setAddBtnHidden(false)
    }

    function addVariant() {
        const newData = {
            productTitle: productSelected.title,
            productId: productSelected.id,
            variantTitle: variantSelected.title,
            variantId: variantSelected.id,
            cost: variantSelected.cost,
            quantity: quantity,
            subtotal: Math.round(variantSelected.cost * quantity * 100, 2) / 100
        }
        setDataToUpdate(oldArray => [...oldArray, '\n', newData])

        resetOrderItem()
    }

    function resetOrderItem() {
        setProductSelected("")
        setVariantSelected("")
        setQuantity(0)
        setProductSelectHidden(true)
        setVariantSelectHidden(true)
        setQuantityHidden(true)
        setAddBtnHidden(true)
        console.log(variantSelect.current.value)
        variantSelect.current.value = "none"
        productSelect.current.value = "none"
        quantityInput.current.value = ""
        quantityInput.current.placeholder = "0"
    }

    if (isLoading) {
        return <div className="loader"></div>;
    }

    return (
        <div>
            <div className="top-content">
                <span className="close" onClick={() =>  props.toggle()}>&times;    </span>
                <h2 >{data[0] ? data[0].name : "Default"}</h2>
                <p>Products: {props.values.products}</p>
                <p>Low Products: {props.values.low_products}</p>
                <button onClick={orderClicked} className="edit-btn" style={{backgroundColor: editColor}}>{btnText}</button>


            </div>

            {isOrdering 

            ?
            <div>
                <div hidden={isOrderItemsHidden} style={orderItemsStyle}>
                    <button style={{display: "block"}} onClick={() => setProductSelectHidden(false)}>New Product</button>
                    <select ref={productSelect} hidden={isProductSelectHidden} onChange={selectChanged} defaultValue="none" id="productFilter" name="productFilter">
                        <option value="none">Choose Product:</option>
                        {data.map((product, index) => {
                            return (
                                <option key={index} label={`${product.title} (${product.stockLevel})`} value={product.id}>{product.variants}</option>
                            )
                        })}
                    </select>
                    <select ref={variantSelect} hidden={isVariantSelectHidden} onChange={selectChanged} defaultValue="none" id="variantFilter" name="variantFilter">
                    <option value="none">Choose Variant:</option>
                        {variantData.map((variant, index) => {
                            return (
                                <option key={index} value={[variant.id, variant.cost]}>{variant.title}</option>
                            )
                        })}
                    </select>
                    <input ref={quantityInput} hidden={isQuantityHidden} placeholder={quantity} onChange={handleChange} />
                    <button hidden={isAddBtnHidden} onClick={addVariant}>Add</button>
                    <table>
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Variant</th>
                                <th>Quantity</th>
                                <th>Cost/unit</th>
                                <th>Subtotal</th>
                            </tr>
                        </thead>
                        <tbody>
                            {dataToUpdate.map((order_item, index) => {
                                if (order_item != "\n") {
                                    return (
                                        <tr key={index}>
                                            <td>{order_item.productTitle}</td>
                                            <td>{order_item.variantTitle}</td>
                                            <td>{order_item.quantity}</td>
                                            <td>${order_item.cost}</td>
                                            <td>${order_item.subtotal}</td>
                                        </tr>
                                    )
                                }
                            })}
                        </tbody>
                    </table>



                </div>
                <div hidden={isOrderOptionsHidden}>
                    {comm_method === "email" 
                    ?
                    <MessageOption type="Email" contact_name={props.values.contact_name} to="stepan.cannuscio@gmail.com" dataToUpdate={dataToUpdate} />
                    :

                    comm_method === "call" 
                    ?
                    <a href="tel:4848885912"><button>Call: 484-888-5912</button></a>

                    :
                    comm_method === "text" 
                    ?
                    <MessageOption type="Text" contact_name={props.values.contact_name} to="4848885912" dataToUpdate={dataToUpdate} />
                    :
                    <p>Not sure about this one!</p>
                    }           



                </div>

            <table style={{textAlign: "center"}}>
            <thead >
                <tr>
                    <th className="table-header">Product</th>
                    <th className="table-header">Quantity</th>
                    <th className="table-header">Purchases (last 30 days)</th>
                    <th className="table-header">Stock Level</th>
                    {/* <th className="table-header">Test</th> */}
                    <th className="table-header">Last Order</th>
                    {/* <th style={tableHeaderStyle}> </th> */}
                </tr>
            </thead>

            <tbody>
            {data.map((item, index) => {
                // var lastOrderData = ""
                var recentOrder = ""
                if (item.status && item.status.split(',')[0] === "completed") {
                    // recentOrder = `Completed | ${new Date(item.completed_date.split(',')[0]).toDateString()} ${'\n'}| ${item.quantity_ordered.split(',')[0]} units`
                    recentOrder = `${item.quantity_ordered.split(',')[0]} units received on ${new Date(item.completed_date.split(',')[0]).toDateString()}`
                } else if (item.status && item.status.split(',')[0] === "fulfilled") {
                    recentOrder = `Fulfilled on ${new Date(item.fulfilled_date.split(',')[0]).toDateString()} ${'\n'} - ${item.tracking.split(',')[0]}`
                } else if (item.status && item.status.split(',')[0] === "submitted") {
                    // recentOrder = `Submitted | ${new Date(item.submitted_date.split(',')[0]).toDateString()} ${'\n'}| ${item.quantity_ordered.split(',')[0]} units`
                    recentOrder = `Submitted order for ${item.quantity_ordered.split(',')[0]} units on ${new Date(item.submitted_date.split(',')[0]).toDateString()}`
                }



                // if (item.status === "completed") {
                //     lastOrderData = item.order_date.slice(0,10) + " | " + item.order_quantity
                // } else if (item.status === "fulfilled") {
                //     lastOrderData = item.order_date.slice(0,10) + " | " + item.order_quantity + " | " 
                // }
                return (
                    <tr key={index}>
                        <td className="table-cell">{item.title}</td>
                        <td className="table-cell">{item.quantity}</td>
                        <td className="table-cell">{item.purchases}</td>
                        <td className="table-cell">{item.stockLevel}</td>
                        {/* <td className="table-cell">{item.status ? item.status + " | " : ""}{item.order_date ? item.order_date + " | " : ""}{item.tracking}</td> */}
                        {/* <td className="table-cell">{item.status ? item.status.split(',')[0] : ""}</td> */}
                        <td className="table-cell">{recentOrder}</td>

                    </tr>
                    ) 
                })
            }
            </tbody>
        </table>
        </div>

            :

            <table style={{textAlign: "center"}}>
                <thead >
                    <tr>
                        <th className="table-header">Product</th>
                        <th className="table-header">Quantity</th>
                        <th className="table-header">Purchases (last 30 days)</th>
                        <th className="table-header">Stock Level</th>
                        {/* <th className="table-header">Test</th> */}
                        <th className="table-header">Last Order</th>
                        {/* <th style={tableHeaderStyle}> </th> */}
                    </tr>
                </thead>

                <tbody>
                {data.map((item, index) => {
                    // var lastOrderData = ""
                    var recentOrder = ""
                    if (item.status && item.status.split(',')[0] === "completed") {
                        // recentOrder = `Completed | ${new Date(item.completed_date.split(',')[0]).toDateString()} ${'\n'}| ${item.quantity_ordered.split(',')[0]} units`
                        recentOrder = `${item.quantity_ordered.split(',')[0]} units received on ${new Date(item.completed_date.split(',')[0]).toDateString()}`
                    } else if (item.status && item.status.split(',')[0] === "fulfilled") {
                        recentOrder = `Fulfilled on ${new Date(item.fulfilled_date.split(',')[0]).toDateString()} ${'\n'} - ${item.tracking.split(',')[0]}`
                    } else if (item.status && item.status.split(',')[0] === "submitted") {
                        // recentOrder = `Submitted | ${new Date(item.submitted_date.split(',')[0]).toDateString()} ${'\n'}| ${item.quantity_ordered.split(',')[0]} units`
                        recentOrder = `Submitted order for ${item.quantity_ordered.split(',')[0]} units on ${new Date(item.submitted_date.split(',')[0]).toDateString()}`
                    }



                    // if (item.status === "completed") {
                    //     lastOrderData = item.order_date.slice(0,10) + " | " + item.order_quantity
                    // } else if (item.status === "fulfilled") {
                    //     lastOrderData = item.order_date.slice(0,10) + " | " + item.order_quantity + " | " 
                    // }
                    return (
                        <tr key={index}>
                            <td className="table-cell">{item.title}</td>
                            <td className="table-cell">{item.quantity}</td>
                            <td className="table-cell">{item.purchases}</td>
                            <td className="table-cell">{item.stockLevel}</td>
                            {/* <td className="table-cell">{item.status ? item.status + " | " : ""}{item.order_date ? item.order_date + " | " : ""}{item.tracking}</td> */}
                            {/* <td className="table-cell">{item.status ? item.status.split(',')[0] : ""}</td> */}
                            <td className="table-cell">{recentOrder}</td>

                        </tr>
                        ) 
                    })
                }
                </tbody>
            </table>
}       
  </div>
    )
}
