async function updateOrder(orderData, id, data) {
    return await axios.post("http://localhost:5000/orders/update",
        {orderData: orderData, id: id, data: data},
        {withCredentials: true})
            .then(res => res.data)
            .catch(err => console.log(err))   
}  


export {loadOrder, loadOrders, updateOrder}
