import React, {useState} from "react";
import Table from "./Table";

export default function Main() {
  // const [topSellingProducts, setProducts] = useState({
  //   "Delta 8 THC Cart": "100",
  //   "Disposable Vape Pen": "50",
  //   "Endocure Pet Chews": "20"
  // })
  const style = {
    backgroundColor: `#e3e4e6`,
    display: `flex`,
    justifyContent: `space-around`,
    margin: `20px`,
    padding: `20px`,
    height: `100vh` /* Force our height since we don't have actual content yet */,
    color: `#1E384D`,
    gridArea: "main",
    backgroundColor: "white",
    textAlign: "center"
  };

  const tableStyle = {
    width: "100%", 
    textAlign: "left", 
    backgroundColor: "#efefef",
    padding: "30px",
    borderRadius: "5px"
  }

  const products = {
    "Delta 8 THC Cart": "10",
    "Disposable Vape Pen": "500",
    "Endocure Pet Chews": "200"
  }

  function sortProducts(array, type) {

    // Create items array
    var items = Object.keys(array).map(key => {
      return [key, array[key]];
    });

    if (type === "high") {
      // Sort the array based on the second element
      items.sort(function(first, second) {
        return second[1] - first[1];
      });
    } else if (type === "low") {
      // Sort the array based on the first element
      items.sort(function(first, second) {
        return first[1] - second[1];
      });
    }

    return items
  }

  const sortedTopSellingProducts = sortProducts(products, "high")



  return (
    <main className="main" style={style}>
      <div>
        <h2>Top Selling Products</h2>
        <table style={tableStyle}>
          <thead >
            <tr>
              <th style={{paddingBottom: "10px"}}>Product</th>
              <th style={{paddingBottom: "10px"}}>Units Sold</th>
            </tr>
          </thead>

          <tbody>
          {/* {Object.keys(products).map((key, index) => {
            return (
            <tr key={index}>
              <td>{key}</td>
              <td>{products[key]}</td>
            </tr>
            )
          })
          } */}

          {sortedTopSellingProducts.map((item, index) => {
            return (
            <tr key={index} style={{padding: "100px"}}>
              <td>{item[0]}</td>
              <td>{item[1]}</td>
            </tr>
            )
          })
          }
           {/* {testProducts.map(product => {
            <tr>
              <td>{product}</td>
            </tr>
          })
          } */}
          </tbody>



          {/* {products.map(product => {
            <tr>
              <td>{product[0]}</td>
              <td>{product[1]}</td>
            </tr>
          })} */}
          {/* <tr>
            <td>Delta 8 THC Cart</td>
            <td>100</td>
          </tr>
          <tr>
            <td>Disposable Vape Pen</td>
            <td>50</td>
          </tr>
          <tr>
            <td>Endocure Pet Chews</td>
            <td>20</td>
          </tr> */}
        </table>
      </div>
      <div>
        <h2>Products Running Low</h2>
      </div>
    </main>
  );
}
