import {useState, useEffect} from "react"
import axios from "axios"
import delta8Image from "../images/delta8-thc-products.jpg"
export default function Popup(props) {

    const [isEditing, setIsEditing] = useState(false)
    const [editColor, setEditColor] = useState("#76c32d")
    const [btnText, setBtnText] = useState("Edit")
    const [product, setProduct] = useState({})
    const [isLoading, setLoading] = useState(true)
    const [dataToUpdate, setDataToUpdate] = useState([])

    const productData = props.product.row.values

    function getProduct() {
        axios.get('http://localhost:5000/products/'+productData.id, {withCredentials: true}).then(res => {
            if (res.data) {
                setProduct(res.data)
                setLoading(false)      
            } else {
                console.log("Error getting product data")
            }
        });
      }
      useEffect(() => {
        getProduct()
      }, []);

    function handleClick() {
        props.toggle()
    }

    function editClicked(e) {
        if (btnText === "Edit") {
            setBtnText("Save")
        } else {
            // Save data
            const updateDict = {}
            dataToUpdate.forEach(item => {
                updateDict[item.id] = item.quantity
            })

            if (dataToUpdate.length > 0) {
                axios.post('http://localhost:5000/products/'+productData.id+'/update', {updateDict}, {withCredentials: true}).then(res => {
                if (res.data === "Product Updated") {
                    setBtnText("Edit")
                    getProduct()
                } else {
                    console.log('error saving')
                }
        })
            } else {
                console.log('no data to save')
            }


        }
        setIsEditing(!isEditing)
        setEditColor(editColor === "#26B1FF" ? "#76c32d" : "#26B1FF")
    }

    const editBtnStyle = {
        color: "white",
        backgroundColor: editColor,
        padding: "7px 30px",
        borderRadius: "5px",
        border: "none",
        fontSize: "16px",
    }

    const tableHeaderStyle = {
        paddingBottom: "10px"
    }

    function editInput(e) {
        if (e.target.value !== e.target.placeholder) {
            // Quantity value has changed for this variant
            const newData = {
                "id": e.target.id,
                "quantity": e.target.value
            }
            setDataToUpdate(oldArray => [...oldArray, newData])
        }
    }

    if (isLoading) {
        return <div className="loader"></div>;
    }


    return (
        <div className="modal">
            <div className="modal_content">
                <span className="close" onClick={handleClick}>&times;    </span>
                <p></p>
                {product.image ? <img style={{width: "10%"}} src={product.image} alt="product image"/> : <p></p>}
                <h2>{product.title}</h2>

                <button onClick={editClicked} style={editBtnStyle}>{btnText}</button>
                <table style={{textAlign: "center"}}>
                    <thead >
                        <tr>
                            <th style={tableHeaderStyle}>Variant</th>
                            <th style={tableHeaderStyle}>Price</th>
                            <th style={tableHeaderStyle}>Quantity</th>
                            <th style={tableHeaderStyle}> </th>
                        </tr>
                    </thead>

                    <tbody>
                    {product.variants.map((variant, index) => {
                            return (
                                <tr key={index}>
                                    <td style={{paddingRight: "20px", border: "none"}}>{variant.title}</td>
                                    <td style={{paddingRight: "20px", border: "none"}}>{variant.price}</td>
                                    {isEditing 
                                        ? 
                                        <td style={{paddingRight: "20px", border: "none"}}>
                                            <input onChange={editInput} id={variant.id} placeholder={variant.quantity}></input>
                                        </td>
                                        :
                                        <td style={{paddingRight: "20px", border: "none"}}>{variant.quantity}
                                        </td>
                                        }

                                </tr>
                            ) 


                        })


                    }
                        {/* {product.variants.length > 1 ? product.variants.map((variant, index) => {
                            return (
                                <tr>
                                    <td style={{paddingRight: "20px", border: "none"}}>{variant.title}</td>
                                    <td style={{paddingRight: "20px", border: "none"}}>30</td>
                                    {isEditing ? <td style={{paddingRight: "20px", border: "none"}}><input value={1}></input></td>: <td style={{paddingRight: "20px", border: "none"}}>1</td>}

                                </tr>
                            ) 


                        })
                        :
                        <p>No variants</p>
                    } */}
                    {/* {sortedTopSellingProducts.map((item, index) => {
                        return (
                        <tr key={index} >
                        <td style={{paddingRight: "20px"}}>{item[0]}</td>
                        <td style={{paddingRight: "20px"}}>{item[1]}</td>
                        </tr>
                        )
                    })
                    } */}
                        {/* <tr >
                            <td style={{paddingRight: "20px", border: "none"}}>700mg</td>
                            <td style={{paddingRight: "20px", border: "none"}}>30</td>
                            {isEditing ? <td style={{paddingRight: "20px", border: "none"}}><input value={1}></input></td>: <td style={{paddingRight: "20px", border: "none"}}>1</td>}


                        </tr>
                        <tr>
                            <td style={{paddingRight: "20px", border: "none"}}>300mg</td>
                            <td style={{paddingRight: "20px", border: "none"}}>10</td>
                            <td style={{paddingRight: "20px", border: "none"}}>30</td>

                        </tr>
                        <tr>
                            <td style={{paddingRight: "20px", border: "none"}}>1,000mg</td>
                            <td style={{paddingRight: "20px", border: "none"}}>80</td>
                            <td style={{paddingRight: "20px", border: "none"}}>62</td>

                        </tr> */}

                    </tbody>
                    </table>
                <div style={{backgroundColor: "#efefef", color: "#1E384D"}}>

                </div>
            </div>
      </div>
    )
}
