import { CSVLink } from "react-csv";
import DownloadIcon from "../images/download.png"

export default function DownloadBtn(props) {
    // console.log("Download Button")

function formatDate() {
    var d = new Date(),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2) 
        month = '0' + month;
    if (day.length < 2) 
        day = '0' + day;

    return [year, month, day].join('');
}

const date = formatDate()

const btnStyle = {
    position: "relative",
    left: "95%",
    bottom: "50px"
}

return (
    <div style={btnStyle}>
    <CSVLink data={props.data} filename={`${props.type}-export-${date}.csv`}>
        <img src={DownloadIcon} alt="download"/>
    </CSVLink>
    </div>
    )


}
