    const [data, setData]= useState([])
    const [productsRunningLow, setProductsRunningLow] = useState(0)

    const history = useHistory();

    function getLowProducts() {
      // console.log('loading Low Products')
      router.loadLowProducts().then(data => {
        // console.log('loaded Low Products')
        if (data) {
          setData(data)
          setProductsRunningLow(data.length)
        }

      })
    }

    function handleClick() {
        // console.lo('clickedd')
        history.push("/products", {products: data})
        // return <Redirect to="/inventory"/>
    }

