import React, {useState, useEffect} from "react"
import Table from "../components/Table"
import Layout from "../components/Layout"
import DownloadBtn from "../components/DownloadBtn"
import ArrowIcon from "../images/arrow.png"
import {useHistory} from "react-router-dom"
import * as router from "./orderAPI";

export default function Order(props) {

    const [data, setData] = useState([])
    const [isLoading, setLoading] = useState(true);

    const history = useHistory()

    // Below 2 styles are from Order.js as well (and maybe another)

    const innerStyle = {
        backgroundColor: "white",
        width: "80%", 
        borderRadius: "5px",
        margin: "20px auto",
        padding: "1% 3%",
        boxShadow: `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
      }

      const outerStyle = {
        gridArea: "main", 

        backgroundColor: "rgb(246,246,247)"
      }



    const columns = React.useMemo(
        () => [
          {
            Header: 'Vendor',
            accessor: 'name',
            sortDescFirst: true
          },
          {
            Header: 'ID',
            accessor: 'id',
          },

          {
            Header: 'Total',
            accessor: 'total',
            sortDescFirst: true,
            sortType: sortMoney
          },
          {
            Header: 'Subtotal',
            accessor: 'subtotal',
            show: false
          },
          {
            Header: 'Discount',
            accessor: 'discount',
            show: false
          },
          {
            Header: 'Submitted',
            accessor: 'submitted_date',
            sortDescFirst: true,
            sortType: sort
          },
          {
            Header: 'Fulfilled',
            accessor: 'fulfilled_date',
            sortDescFirst: true
          },
          {
            Header: 'Completed',
            accessor: 'completed_date',
            sortDescFirst: true
          },
          {
            Header: 'Status',
            accessor: 'status',
            sortDescFirst: true
          },


        ],
        []
      )


    function sortMoney(rowA, rowB) {
      console.log(rowA.values)
      console.log(rowB.values)

      var aValues = "$0"
      var bValues = "$0"
      if (rowA.values.total) {
        aValues = rowA.values.total
      }
      if (rowB.values.total) {
        bValues = rowB.values.total
      }

      // if (rowA.values.total && !rowB.values.total) {
      //   console.log('sd')
      //   return 1
      // } else if (!rowA.values.total && rowB.values.total) {
      //   console.log('sda')
      //   return -1
      // } else if (!rowA.values.total && !rowB.values.total) {
      //   console.log('sdb')
      //   return 1

        if (Number(aValues.replace(/(^\$|,)/g,'')) > Number(bValues.replace(/(^\$|,)/g,''))) {
          console.log('ccc')
          return 1
      } else {
          return -1
      }

      // try {
      //   return Number(aValues.replace(/(^\$|,)/g,'')) > Number(bValues.replace(/(^\$|,)/g,'')) ? 1 : -1
      // } catch {
      //   console.log(aValues.replace(/(^\$|,)/g,''))
      //   console.log('bigErr')
      // }


      function sortDates(a, b) {
        // dates.sort(function(a, b){
        //     var aa = a.split('/').reverse().join(),
        //         bb = b.split('/').reverse().join();
        //     return aa < bb ? -1 : (aa > bb ? 1 : 0);
        // });

        // dates.sort(function (a, b) {
        //     // '01/03/2014'.split('/')
        //     // gives ["01", "03", "2014"]
            a = a.split('/');
            b = b.split('/');
            return a[2] - b[2] || a[0] - b[0] || a[1] - b[1]
        // });


    }





  }

    function getOrders() {
      setLoading(true)
      router.loadOrders().then(data => {
        if (data) {
          console.log(data)
          setData(data)
        }    
        setLoading(false)   
      }) 
      }


      useEffect(() => {
        getOrders()
      }, []);

      if (isLoading) {
        return <div className="loader"></div>;
      }


    return (
        <Layout user={props.user}>
            <div style={outerStyle}>

            <div style={innerStyle}>
            <button className="circle-btn" style={{position: "relative", left: "0px"}} onClick={() => history.goBack()}><img src={ArrowIcon} /></button>
                  <h1>Orders</h1>
                  <DownloadBtn data={data} type="orders"/>
                  {/* <Table reloadData={() => getProducts('reload')} columns={columns} data={products} search={true} popupEnabled={true} perPage={10}/> */}
                  <Table reloadData={() => getOrders()} initialSearch="" columns={columns} type="all-orders" data={data} search={true} popupEnabled={true} perPage={10}/>
            </div>
            </div>
        </Layout>


    )

}
