import React, {useEffect, useState} from "react"
import Layout from "../components/Layout"
import Table from "../components/Table"
import Calendar from "./Calendar"
import {useHistory} from "react-router-dom"
import * as router from "./vendorAPI"

export default function Vendor(props) {
  console.log(props)

    const [data, setData] = useState([])

    const outerStyle = {
        gridArea: "main", 

        backgroundColor: "rgb(246,246,247)"
      }

      const innerStyle = {
        backgroundColor: "white",
        width: "80%", 
        borderRadius: "5px",
        margin: "20px auto",
        padding: "1% 3%",
        boxShadow: `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
      }

      const calendarStyle = {
        border: "none", 
        // margin: "0 auto",
        width: "100%", 
        height: "400px",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,

        borderRadius: "5px",
        // height: "600px"
      }

      const addPopupStyle = {
        position: "absolute",
        zIndex: "10",
        backgroundColor: "white",
        padding: "10px 20px",
        boxShadow:
        `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
        left: "45%",

    }

    const history = useHistory()

    const columns = React.useMemo(
        () => [
          {
            Header: 'Vendor',
            accessor: 'name',
            sortDescFirst: true
          },
          {
            Header: 'ID',
            accessor: 'id',
            show: false
          },
          {
            Header: 'Contact Name',
            accessor: 'contact_name',
            show: false
          },
          {
            Header: 'Communication Method',
            accessor: 'comm_method',
            show: false
          },
          {
            Header: 'Email',
            accessor: 'email',
            show: false
          },
          {
            Header: 'Phone',
            accessor: 'phone',
            show: false
          },

          {
            Header: 'Products',
            accessor: 'products',
            sortDescFirst: true
          },

          {
            Header: 'Low Products',
            accessor: 'low_products',
            sortDescFirst: true
          },

        ],
        []
      )

      function getData() {
        router.loadVendors().then(data => {
            if (data) {            
                setData(data)
            }         
        })
      }


      useEffect(() => {
        getData()
      }, []);


    return (

        <Layout user={props.user}>
        <div style={outerStyle}>
            <div>
                <Calendar data={data}/>
            </div>
            <div style={{textAlign: "center"}}>
              <button className="btn" onClick={() => history.push("/orders")}>View All Orders</button>
            </div>   
            <div style={innerStyle}>
                  <h1>Vendors</h1>
                  <button className="circle-btn" onClick={() => setAddPopupHidden(!isAddPopupHidden)}>+</button>
       <div style={addPopupStyle} hidden={isAddPopupHidden}>
       <span className="close" style={{color: "#1E384D"}} onClick={() => setAddPopupHidden(!isAddPopupHidden)}>&times;    </span>
        <h2>Create Event</h2>
        <input onChange={handleChange} style={inputStyle} value={eventTitle} type="text" id="event_title" placeholder="Event Title" />
         <textarea onChange={handleChange} style={inputStyle} value={eventDescription} rows="5" id="event_description" placeholder="Event Description" />

         <input onChange={handleChange} style={inputStyle} type="datetime-local" id="event_start" name="event_start" value={eventStart}/>

         <input onChange={handleChange} style={inputStyle} type="datetime-local" id="event_end" name="event_end" value={eventEnd} />

         <p style={{display: "inline"}}>All day?</p><input style={{display: "inline"}} onChange={() => setAllDay(!allDay)}type="checkbox"/>
         <select style={inputStyle} defaultValue="Choose Vendor:" onChange={handleChange} id="vendor">
             <option value="Choose Vendor:">Choose Vendor:</option>
             {data.map((vendor, id) => {
                 return (
                    <option key={id} value={vendor.id}>{vendor.name}</option>
                 )

             })}

            {/* <option value="">{}</option> */}
            {/* <option value="month" selected>Month</option>
            <option value="week">Week</option>
            <option value="year">Year</option> */}
        </select>
         <button style={submitStyle} onClick={createSale}>Create Sale</button>
        </div>
                  <Table reloadData={() => getData()} user={props.user} initialSearch={props.location.state ? props.location.state.vendor : ""} columns={columns} type="vendor" data={data} search={true} popupEnabled={true} perPage={10}/>
            </div>
        </div>


          {/* <h1>Order</h1> */}

        </Layout>
    )
}
