import {useState, useEffect} from "react"
import * as router from "./orderAPI"

export default function OrderPopup(props) {


    const [data, setData] = useState([])
    const [isLoading, setLoading] = useState(false)

    const [btnText, setBtnText] = useState("Edit")
    const [isEditing, setIsEditing] = useState(false)
    const [editColor, setEditColor] = useState("#76c32d")
    const [isTrackingHidden, setTrackingHidden] = useState(true)
    const [status, setStatus] = useState(null)
    const [tracking, setTracking] = useState(null)
    const [discount, setDiscount] = useState(null)
    const [dataToUpdate, setDataToUpdate] = useState([])
    const [itemIds, setItemIds] = useState([])

    const formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
        minimumFractionDigits: 0
      })


    const inputStyle = {
        display: "block",
        margin: "20px auto",
        padding: "10px 20px",
        fontSize: "16px",
        // width: "0%",
        border: "none",
        borderBottom: "1px solid #76c32d"
    }

    // console.lo(data)

    function getOrder() {
        // console.lo('are we even calling')
        // console.log('loading Order')
        setLoading(true)
        router.loadOrder(props.values.id).then(data => {
            // console.log('loaded Order')
            if (data) {
                setData(data)
                setLoading(false)      
            } else {
                // console.lo("Error getting product data")
            }
        })

      }

      useEffect(() => {
        getOrder()
    }, []);

    if (isLoading) {
        return <div className="loader"></div>;
    }

    function editBtnClicked(e) {
        if (btnText === "Edit") {
            // setStatusSelectHidden(false)
            // console.lo(data)
            setBtnText("Save")
        } else {
            // Save data
            // console.lo('need to save data')
            const updateDict = {}
            dataToUpdate.forEach(item => {
                if (item.quantity) {
                    updateDict[item.id] = item.quantity
                }

            })

            router.updateOrder(tracking, discount, status, props.values.id, data, updateDict).then(data => {
                if (data === "Success") {
                    // console.log('variants updated -')
                    setBtnText("Edit")
                    getOrder()
                    setTrackingHidden(true)
                } else {
                    // console.log('error saving')
                    setBtnText("Edit")
                }
            })            

        }
        setIsEditing(!isEditing)
        setEditColor(editColor === "#26B1FF" ? "#76c32d" : "#26B1FF")
    }

    function statusChanged(e) {
        setStatus(e.target.value)

        if (e.target.value === "fulfilled") {
            setTrackingHidden(false)
        } else {
            setTrackingHidden(true)
        }

    }

    function editInput(e) {
        if (e.target.value !== e.target.placeholder) {
            const orderItemId = e.target.id.split('-')[0].trim()

            const newData = {
                id: orderItemId
            }
            // Quantity value has changed for this variant
            if (e.target.id.includes("quantity")) {
                newData.quantity = e.target.value
            } else {
                newData.cost = e.target.value
            }





            setDataToUpdate(oldArray => [...oldArray, newData])

        }
    }

    function capitalizeFirstLetter(string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
      }


    return (
        <div>
            <div className="top-content">
                <span className="close" onClick={() =>  props.toggle()}>&times;    </span>
                <h2 style={{fontSize: "2rem"}}>Order #{data.length > 0 ? data[0].order_id : "Error loading data"}</h2>
                <p style={{fontSize: "1.4rem"}}>Vendor: {data.length > 0 ? data[0].name : ""}</p>
                {/* <hr style={{width: "25%", border: "1px solid #76c32d"}}/> */}
                <div style={{border: "1px solid #1E384D", borderRadius: "5px"}}>
                    <p style={{display: "inline-block", margin: "10px",fontSize: "1.1rem"}}>Subtotal: {data.length > 0 ? formatter.format(data[0].order_subtotal) : ""}</p>
                    <p style={{display: "inline-block", margin: "10px", fontSize: "1.1rem"}}>Discount: {data.length > 0 ? formatter.format(data[0].order_discount) : ""}</p>
                    <p style={{display: "inline-block", margin: "10px", fontSize: "1.1rem"}}>Total: {data.length > 0 ? formatter.format(data[0].order_total) : ""}</p>
                </div>

                <p style={{fontSize: "1.1rem", color: data.length > 0 && data[0].status === "submitted" ? "red" : data.length > 0 && data[0].status==="fulfilled" ? "orange" : "green"}}>Status: {data.length > 0 ? capitalizeFirstLetter(data[0].status) : ""}</p>
                {/* <hr style={{width: "25%", border: "1px solid #76c32d"}}/> */}
                <div style={{border: "1px solid #1E384D", borderRadius: "5px"}}>
                    <p style={{display: "inline-block", margin: "10px", fontSize: "1.1rem"}}>Submitted: {data.length > 0 ? data[0].submitted_date : ""}</p>
                    <p style={{display: "inline-block", margin: "10px", fontSize: "1.1rem"}}>Fulfilled: {data.length > 0 ? data[0].fulfilled_date : ""}</p>
                    <p style={{display: "inline-block", margin: "10px", fontSize: "1.1rem"}}>Completed: {data.length > 0 ? data[0].completed_date : ""}</p>
                </div>

                {isEditing 
                    ?
                <div style={{textAlign: "center"}}>
                    <select style={inputStyle} onChange={statusChanged}>
                        <option value="submitted">Submitted</option>
                        <option value="fulfilled">Fulfilled</option>
                        <option value="completed">Completed</option>
                    </select>
                    <input style={inputStyle} hidden={isTrackingHidden} className="table-cell" placeholder="Tracking" onChange={(e) => setTracking(e.target.value)}></input>
                    <input style={inputStyle} hidden={isTrackingHidden} className="table-cell" placeholder="Discount (i.e. 180.02)" onChange={(e) => setDiscount(e.target.value)}></input>
                    </div>
                :
                <p></p>

                }

                <button hidden={isTrackingHidden ? data[0] && data[0].status === "completed" : false} onClick={editBtnClicked} className="edit-btn" style={{backgroundColor: editColor}}>{btnText}</button>
                {/* <p>Products: {props.values.products}</p>
                <p>Low Products: {props.values.low_products}</p>
                <button onClick={orderClicked} className="edit-btn" style={{backgroundColor: editColor}}>{btnText}</button>    */}
            </div>

            <table style={{textAlign: "center"}}>
                <thead >
                    <tr>
                        <th className="table-header">Product</th>
                        <th className="table-header">Variant</th>
                        <th className="table-header">Quantity</th>
                        <th className="table-header">Cost</th>
                        <th className="table-header">Subtotal</th>
                        <th className="table-header">Discount</th>
                        <th className="table-header">Total</th>
                        {/* <th style={tableHeaderStyle}> </th> */}
                    </tr>
                </thead>

                <tbody>
                {data.map((item, index) => {
                    return (
                        <tr key={index}>
                            <td className="table-cell">{item.product}</td>
                            <td className="table-cell">{item.variant}</td>

                            <td className="table-cell">
                                    {item.quantity}
                                </td>

                                <td className="table-cell">
                                    {formatter.format(item.cost)}
                                </td>
                            {/* {isEditing 
                                ? 
                                <td className="table-cell">
                                    <input className="table-cell" onChange={editInput} id={`${item.order_item_id} - quantity`} placeholder={item.quantity}></input>
                                </td>

                                :
                                <td className="table-cell">
                                    {item.quantity}
                                </td>
                                }
                                {isEditing 
                                ? 
                                <td className="table-cell">
                                    <input className="table-cell" onChange={editInput} id={`${item.order_item_id} - cost`} placeholder={item.cost}></input>
                                </td>

                                :
                                <td className="table-cell">
                                    {formatter.format(item.cost)}
                                </td>
                                } */}
                            <td className="table-cell">{formatter.format(item.subtotal)}</td>
                            <td className="table-cell">{formatter.format(item.discount)}</td>
                            <td className="table-cell">{formatter.format(item.total)}</td>

                        </tr>
                        ) 
                    })
                }
                </tbody>
            </table>
        </div>


    )
}
