import React, {useEffect, useState} from "react"
import Layout from "../components/Layout"
import Table from "../components/Table"
import DownloadBtn from "../components/DownloadBtn"
import * as router from "./productAPI"

export default function Product(props) {
    const [products, setProducts] = useState([])
    const [isLoading, setLoading] = useState(false)
    // console.log(props)

    const outerStyle = {
      gridArea: "main", 

      backgroundColor: "rgb(246,246,247)"
    }

    const innerStyle = {
      backgroundColor: "white",
      width: "80%", 
      borderRadius: "5px",
      margin: "20px auto",
      padding: "1% 3%",
      boxShadow:
    `0 1px 4px rgba(0, 0, 0, 0.2), 0 0 10px rgba(10, 0, 0, 0.1)`,
    }

    function getProducts(reload) {
      console.log('calling getProducts')
      console.log(reload)
      console.log(props.location)
      setLoading(true)
      if (props.location.state) {
        if (reload) {
          router.loadLowProducts().then(data => {
            if (data) {
                setProducts(data)
            }
        })
        } else {
          setProducts(props.location.state.products)
        }

      } else {
        router.loadProducts().then(data => {
          if (data) {
            setProducts(data)
          }
        })
       }  
       setLoading(false) 
      }

      useEffect(() => {
        getProducts()
      }, []);


    const columns = React.useMemo(
        () => [
          {
            Header: 'ID',
            accessor: 'id',
            show: false
          },
          {
            Header: 'Product Name',
            accessor: 'title',
            sortDescFirst: true
          },
          {
            Header: 'Shopify ID',
            accessor: 'shopify_id',
            sortDescFirst: true
          },
          {
            Header: 'Vendor',
            accessor: 'vendor',
            sortDescFirst: true
          },
          {
            Header: 'Vendor_ID',
            accessor: 'vendor_id',
            show: false
          },
          {
            Header: 'Variants',
            accessor: 'variants',
            sortDescFirst: true
          },
          {
            Header: 'Quantity',
            accessor: 'quantity',
            sortDescFirst: true
          },
          {
            Header: 'Stock Level',
            accessor: 'stockLevel',
            disableSortBy: true
          },
        ],
        []
      )

      if (isLoading) {
        return <div className="loader"></div>;
      }


    return (

        <Layout user={props.user}>
          <div style={outerStyle}>
          <div style={innerStyle}>
                <h1>Products</h1>
                <DownloadBtn data={products} type="products" />
                <Table reloadData={() => getProducts('reload')} initialSearch="" type="products" columns={columns} data={products} search={true} popupEnabled={true} perPage={10}/>
            </div>
          </div>

        </Layout>
    )
}
