import React, {useState, useEffect} from "react"
import Table from "../components/Table"
import * as router from "./homeAPI";

export default function MiniTable(props) {

    const [data, setData] = useState([])

    function getTopSellingProducts() {
      console.log('loading Top Selling Products')
      router.loadTopSellingProducts(props.startDate, props.endDate).then(data => {
        console.log('loaded Top Selling Products')
        if (data) {
          // console.lo(data)
          setData(data)
        }
      });
    }

    function sortMoney(rowA, rowB) {

        if (Number(rowA.values.revenue_earned.replace(/(^\$|,)/g,'')) > Number(rowB.values.revenue_earned.replace(/(^\$|,)/g,''))) {
            return 1
        } else {
            return -1
        }
    }

    useEffect(() => {
      getTopSellingProducts()
      }, [props.changed]);


    const columns = React.useMemo(
        () => [
          {
            Header: 'Product Name',
            accessor: 'title',
            sortDescFirst: true
          },
          {
            Header: 'Units Sold',
            accessor: 'units_sold',
            sortDescFirst: true
          },
          {
            Header: 'Revenue Earned',
            accessor: 'revenue_earned',
            sortDescFirst: true,
            sortType: sortMoney
          },
        ],
        []
      )
    //   const data = []

    return (
        // <div style={{margin: "40px 0", padding: "20px 40px", backgroundColor: "#efefef", borderRadius: "5px"}}>

        // <a onClick={() => handleClick(1)} style={{color: firstTabColor}}href="#">Top Selling - Revenue</a><span>   |   </span>
        // <a onClick={() => handleClick(2)} style={{color: secondTabColor}}href="#">Top Selling - Units</a> 

         <Table columns={columns} data={data} search={false} popupEnabled={false} perPage={5}/>
        //  </div>
    )
}
