import './App.css';
import {BrowserRouter, Switch, Route, Redirect} from 'react-router-dom'
import Login from './components/Login'
import Home from './components/Home'
import {useState} from "react"

function App() {
  const [color, setColor] = useState("#1E384D")
  const [auth, setAuth] = useState(false)
  // const [isAuthenticated, setIsAuthenticated]

  // state = { message: "" }
  function authCallback(isAuthenticated) {
    console.log('function authCallback')
    setAuth(true)

  }

  // GET VALUE OF ISAUTHORIZED BY MAKING REQUEST TO BACKEND /AUTH/USER TO SEE IF CURRENTLY AUTHORIZED



  function PrivateRoute ({component: Component, authed, ...rest}) {
    console.log(authed)
    return (
      <Route
        {...rest}
        render={(props) => {
          axios.get("http://localhost:5000/user", { withCredentials: true })
          .then(console.log)
          .catch(console.error);
          if (authed === true) {
            <Component {...props} />
          } else {
            <Redirect to={{pathname: '/login', state: {from: props.location}}} />
          }
          }
        }
      />
    )
  }


  return (
    <div style={{backgroundColor: color, height: "100vh"}}>

      <BrowserRouter>
      <Switch>
        <Route path="/login">
          <Login parentCallback = {authCallback}/>
        </Route>
        <PrivateRoute path='/' authed={auth} component={Home} />
        {/* <PrivateRoute authed={false} path='/'>
          <Home />
        </PrivateRoute> */}
        {/* <Route path="/">
          <Home />
        </Route> */}
      </Switch>
      </BrowserRouter>

    </div>
  );
}

export default App;
